routine.definition:
DECLARE
	schdf text;
	tbldf text;
	trig text;
BEGIN
IF NOT (action_name ILIKE ANY(ARRAY['ENABLE','DISABLE'])) THEN
	RAISE NOTICE '% not a valid input',action_name;
	RETURN;
ELSE
	FOR schdf,tbldf, trig IN
		SELECT
		c.relnamespace ::regnamespace::text,
		c.relname,
		t.tgname
		FROM   pg_trigger t
		INNER JOIN pg_class c on t.tgrelid=c.oid
		INNER JOIN pg_proc p on t.tgfoid=p.oid
		WHERE p.proname  LIKE 'modification_%'
		AND p.pronamespace::regnamespace::text = 'tww_app'
		LOOP
			EXECUTE FORMAT('ALTER TABLE %I.%I %s TRIGGER %I',schdf,tbldf,upper(action_name),trig);
		END LOOP;

	RETURN;
END IF;
END;

DECLARE
	schdf text;
	tbldf text;
	trig text;
BEGIN
IF NOT (action_name ILIKE ANY(ARRAY['ENABLE','DISABLE'])) THEN
	RAISE NOTICE '% not a valid input',action_name;
	RETURN;
ELSE
	FOR schdf,tbldf, trig IN
		SELECT
		c.relnamespace ::regnamespace::text,
		c.relname,
		t.tgname
		FROM   pg_trigger t
		INNER JOIN pg_class c on t.tgrelid=c.oid
		INNER JOIN pg_proc p on t.tgfoid=p.oid
		WHERE p.proname  LIKE 'symbology_%'
		and p.pronamespace::regnamespace::text LIKE 'tww_ap_'
		LOOP
			EXECUTE FORMAT('ALTER TABLE %I.%I %s TRIGGER %I',schdf,tbldf,upper(action_name),trig);
		END LOOP;
		RETURN;
END IF;
END;

DECLARE
        rec_jsonb jsonb;
    BEGIN

	  rec_jsonb := (
          SELECT jsonb_object_agg(key, to_jsonb(rec)->key)
          FROM jsonb_object_keys(to_jsonb(rec)) key
          WHERE LEFT(key, 2) = prefix
		  AND NOT(ltrim(key, prefix||'_') =ANY(ignored_postfix))
        );

      -- Check if all remaining values are NULL
      RETURN jsonb_strip_nulls(rec_jsonb)::text = '{}' OR jsonb_strip_nulls(rec_jsonb)::text IS NULL;
	  END;

DECLARE _disabled_count numeric;
BEGIN
  SELECT count(*) into _disabled_count
		FROM   pg_trigger t
		INNER JOIN pg_class c on t.tgrelid=c.oid
		INNER JOIN pg_proc p on t.tgfoid=p.oid
		WHERE
		p.proname  LIKE 'modification_%'
		AND p.pronamespace::regnamespace::text = 'tww_app'

		AND t.tgenabled = 'D';
  RETURN _disabled_count=0;
END;

DECLARE _disabled_count numeric;
BEGIN
  SELECT count(*) into _disabled_count
		FROM   pg_trigger t
		INNER JOIN pg_class c on t.tgrelid=c.oid
		INNER JOIN pg_proc p on t.tgfoid=p.oid
		WHERE p.proname  LIKE 'symbology_%'
		and p.pronamespace::regnamespace::text LIKE 'tww_ap_'
		AND t.tgenabled = 'D';
  RETURN _disabled_count=0;
END;

DECLARE
    sch text;
    tbl text;
	col text;
	ttp text;
BEGIN
	FOR sch,tbl,col,ttp IN
	SELECT
       t.table_schema,
	   c.table_name,
	   c.column_name,
	   CASE WHEN t.table_type = 'BASE TABLE' then 'TABLE' ELSE t.table_type END

    FROM information_schema.columns c
	LEFT JOIN information_schema.tables t
	ON c.table_name = t.table_name
    and c.table_schema = t.table_schema
    WHERE c.column_name = NEW.fieldname
      and ((c.table_schema ='tww_od'
	  and t.table_type = 'BASE TABLE')
	  OR  (c.table_schema ='tww_app'
	  and t.table_type = 'VIEW'))
    LOOP
        EXECUTE format($$ ALTER %4$s %1$I.%2$I ALTER COLUMN %3$I SET DEFAULT tww_app.get_default_values('%3$s') $$, sch,tbl,col,ttp);
    END LOOP;
	RETURN NEW;
END;

DECLARE
    sch text;
    tbl text;
	col text;
	ttp text;
BEGIN
	FOR sch,tbl,col,ttp IN
	SELECT
       t.table_schema,
	   c.table_name,
	   c.column_name,
	   CASE WHEN t.table_type = 'BASE TABLE' then 'TABLE' ELSE t.table_type END

    FROM information_schema.columns c
	LEFT JOIN information_schema.tables t
	ON c.table_name = t.table_name
    and c.table_schema = t.table_schema
    WHERE c.column_name = OLD.fieldname
      and ((c.table_schema ='tww_od'
	  and t.table_type = 'BASE TABLE')
	  OR  (c.table_schema ='tww_app'
	  and t.table_type = 'VIEW'))
    LOOP
        EXECUTE format($$ ALTER %4$s %1$I.%2$I ALTER COLUMN %3$I DROP DEFAULT $$, sch,tbl,col,ttp);
    END LOOP;
	RETURN NULL;
END;

BEGIN
  DELETE FROM tww_od.access_aid WHERE obj_id = OLD.obj_id;
  DELETE FROM tww_od.structure_part WHERE obj_id = OLD.obj_id;
RETURN NULL;
END;

BEGIN INSERT INTO tww_od.structure_part ( obj_id , fk_dataowner , fk_provider , fk_wastewater_structure , identifier , last_modification , remark , renovation_demand ) VALUES ( COALESCE( NEW.obj_id, tww_app.generate_oid('tww_od'::text, 'access_aid'::text) ) , NEW.fk_dataowner , NEW.fk_provider , NEW.fk_wastewater_structure , CASE WHEN NEW.identifier IS NOT NULL AND NEW.identifier::text <> '' THEN NEW.identifier ELSE COALESCE( NEW.obj_id, tww_app.generate_oid('tww_od'::text, 'access_aid'::text) ) END , NEW.last_modification , NEW.remark , NEW.renovation_demand ) RETURNING obj_id INTO NEW.obj_id;
INSERT INTO tww_od.access_aid ( obj_id , kind ) VALUES ( NEW.obj_id , NEW.kind );
RETURN NEW; END;

