DECLARE
    BEGIN
      DELETE FROM tww_od.wastewater_structure WHERE obj_id = OLD.obj_id;
    RETURN OLD;
    END;

BEGIN
NEW.identifier = COALESCE(NEW.identifier, NEW.obj_id);
INSERT INTO tww_od.wastewater_structure ( obj_id -- , _bottom_label -- , _cover_label , _depth -- , _input_label -- , _label -- , _output_label , accessibility , condition_score , contract_section -- , detail_geometry3d_geometry , elevation_determination , financing , fk_dataowner -- , fk_main_cover -- , fk_main_wastewater_node , fk_operator , fk_owner , fk_provider , gross_costs , identifier , inspection_interval , last_modification , location_name , records , remark , renovation_necessity , replacement_value , rv_base_year , rv_construction_type , status , status_survey_year , structure_condition , subsidies , urgency_figure , year_of_construction , year_of_replacement ) VALUES ( NEW.obj_id -- , NEW._bottom_label -- , NEW._cover_label , NEW._depth -- , NEW._input_label -- , NEW._label -- , NEW._output_label , NEW.accessibility , NEW.condition_score , NEW.contract_section -- , NEW.detail_geometry3d_geometry , NEW.elevation_determination , NEW.financing , NEW.fk_dataowner -- , NEW.fk_main_cover -- , NEW.fk_main_wastewater_node , NEW.fk_operator , NEW.fk_owner , NEW.fk_provider , NEW.gross_costs , NEW.identifier , NEW.inspection_interval , NEW.last_modification , NEW.location_name , NEW.records , NEW.remark , NEW.renovation_necessity , NEW.replacement_value , NEW.rv_base_year , NEW.rv_construction_type , NEW.status , NEW.status_survey_year , NEW.structure_condition , NEW.subsidies , NEW.urgency_figure , NEW.year_of_construction , NEW.year_of_replacement );
CASE WHEN NEW.ws_type = 'wwtp_structure' THEN -- wwtp_structure INSERT INTO tww_od.wwtp_structure ( obj_id , fk_waste_water_treatment_plant , kind ) VALUES ( NEW.obj_id , NEW.wt_fk_waste_water_treatment_plant , NEW.wt_kind );
-- small_treatment_plant WHEN NEW.ws_type = 'small_treatment_plant' THEN INSERT INTO tww_od.small_treatment_plant ( obj_id , approval_number , function , installation_number , remote_monitoring ) VALUES ( NEW.obj_id , NEW.sm_approval_number , NEW.sm_function , NEW.sm_installation_number , NEW.sm_remote_monitoring );
-- drainless_toilet WHEN NEW.ws_type = 'drainless_toilet' THEN INSERT INTO tww_od.drainless_toilet ( obj_id , kind ) VALUES ( NEW.obj_id , NEW.to_kind );
ELSE RAISE NOTICE 'Wastewater structure type not known (%)', NEW.ws_type; -- ERROR END CASE;
INSERT INTO tww_app.vw_wastewater_node ( -- _function_hierarchic -- _status -- _usage_current backflow_level_current , bottom_level , elevation_accuracy , fk_dataowner , fk_hydr_geometry , fk_provider , fk_wastewater_structure , function_node_amelioration , identifier , last_modification , obj_id , remark , situation3d_geometry , wwtp_number ) VALUES ( -- NEW.wn__function_hierarchic -- NEW.wn__status -- NEW.wn__usage_current NEW.wn_backflow_level_current , NEW.wn_bottom_level , NEW.wn_elevation_accuracy , COALESCE(NULLIF(NEW.wn_fk_dataowner,''), NEW.fk_dataowner) -- fk_dataowner , NEW.wn_fk_hydr_geometry , COALESCE(NULLIF(NEW.wn_fk_provider,''), NEW.fk_provider) -- fk_provider , NEW.obj_id -- fk_wastewater_structure , NEW.wn_function_node_amelioration , COALESCE(NULLIF(NEW.wn_identifier,''), NEW.identifier) -- identifier , NOW() -- last_modification , NEW.wn_obj_id , NEW.wn_remark , ST_SetSRID(ST_MakePoint(ST_X(NEW.situation3d_geometry), ST_Y(NEW.situation3d_geometry), 'nan'), 2056 ) -- situation3d_geometry , NEW.wn_wwtp_number );
UPDATE tww_od.wastewater_structure SET fk_main_wastewater_node = NEW.wn_obj_id WHERE obj_id = NEW.obj_id;
INSERT INTO tww_app.vw_cover ( brand , cover_shape , diameter , fastening , fk_dataowner , fk_provider , fk_wastewater_structure , identifier , last_modification , level , material , obj_id , positional_accuracy , remark , renovation_demand , situation3d_geometry , sludge_bucket , venting ) VALUES ( NEW.co_brand , NEW.co_shape , NEW.co_diameter , NEW.co_fastening , NEW.fk_dataowner -- fk_dataowner , NEW.fk_provider -- fk_provider , NEW.obj_id -- fk_wastewater_structure , COALESCE(NULLIF(NEW.co_identifier,''), NEW.identifier) -- identifier , NOW() -- last_modification , NEW.co_level , NEW.co_material , NEW.co_obj_id , NEW.co_positional_accuracy , NEW.co_remark , NEW.co_renovation_demand , ST_SetSRID(ST_MakePoint(ST_X(NEW.situation3d_geometry), ST_Y(NEW.situation3d_geometry), 'nan'), 2056 ) -- situation3d_geometry , NEW.co_sludge_bucket , NEW.co_venting );
UPDATE tww_od.wastewater_structure SET fk_main_cover = NEW.co_obj_id WHERE obj_id = NEW.obj_id;
RETURN NEW; END;

DECLARE dx float; dy float; BEGIN UPDATE tww_od.cover co SET brand = NEW.co_brand , cover_shape = NEW.co_shape , diameter = NEW.co_diameter , fastening = NEW.co_fastening , level = NEW.co_level , material = NEW.co_material , positional_accuracy = NEW.co_positional_accuracy -- , situation3d_geometry = NEW.co_situation3d_geometry , sludge_bucket = NEW.co_sludge_bucket , venting = NEW.co_venting WHERE obj_id = OLD.co_obj_id; UPDATE tww_od.structure_part sp SET fk_dataowner = NEW.fk_dataowner , fk_provider = NEW.fk_provider -- , fk_wastewater_structure = NEW.co_fk_wastewater_structure , identifier = NEW.co_identifier , last_modification = NEW.last_modification , remark = NEW.co_remark , renovation_demand = NEW.co_renovation_demand WHERE obj_id = OLD.co_obj_id; UPDATE tww_od.wastewater_structure ws SET obj_id = NEW.obj_id -- , _bottom_label = NEW._bottom_label -- , _cover_label = NEW._cover_label -- , _depth = NEW._depth -- , _input_label = NEW._input_label -- , _label = NEW._label -- , _output_label = NEW._output_label , accessibility = NEW.accessibility , condition_score = NEW.condition_score , contract_section = NEW.contract_section -- , detail_geometry3d_geometry = NEW.detail_geometry3d_geometry , elevation_determination = NEW.elevation_determination , financing = NEW.financing , fk_dataowner = NEW.fk_dataowner -- , fk_main_cover = NEW.fk_main_cover -- , fk_main_wastewater_node = NEW.fk_main_wastewater_node , fk_operator = NEW.fk_operator , fk_owner = NEW.fk_owner , fk_provider = NEW.fk_provider , gross_costs = NEW.gross_costs , identifier = NEW.identifier , inspection_interval = NEW.inspection_interval -- , last_modification = NEW.last_modification , location_name = NEW.location_name , records = NEW.records , remark = NEW.remark , renovation_necessity = NEW.renovation_necessity , replacement_value = NEW.replacement_value , rv_base_year = NEW.rv_base_year , rv_construction_type = NEW.rv_construction_type , status = NEW.status , status_survey_year = NEW.status_survey_year , structure_condition = NEW.structure_condition , subsidies = NEW.subsidies , urgency_figure = NEW.urgency_figure , year_of_construction = NEW.year_of_construction , year_of_replacement = NEW.year_of_replacement WHERE obj_id = OLD.obj_id; UPDATE tww_od.wastewater_node wn SET -- _function_hierarchic = NEW.wn__function_hierarchic -- _status = NEW.wn__status -- _usage_current = NEW.wn__usage_current backflow_level_current = NEW.wn_backflow_level_current , bottom_level = NEW.wn_bottom_level , elevation_accuracy = NEW.wn_elevation_accuracy , fk_hydr_geometry = NEW.wn_fk_hydr_geometry , function_node_amelioration = NEW.wn_function_node_amelioration -- , situation3d_geometry = NEW.wn_situation3d_geometry , wwtp_number = NEW.wn_wwtp_number WHERE obj_id = OLD.wn_obj_id;
IF OLD.ws_type <> NEW.ws_type THEN CASE WHEN OLD.ws_type <> 'unknown' THEN BEGIN EXECUTE FORMAT('DELETE FROM tww_od.%I WHERE obj_id = %L',OLD.ws_type,OLD.obj_id); END; END CASE;
CASE WHEN NEW.ws_type = ANY(ARRAY['manhole','special_structure','discharge_point','infiltration_installation','drainless_toilet','wwtp_structure','small_treatment_plant']) THEN BEGIN EXECUTE FORMAT('INSERT INTO tww_od.%I(obj_id) VALUES (%L)',NEW.ws_type,OLD.obj_id); END; END CASE; END IF;
CASE WHEN NEW.ws_type = 'wwtp_structure' THEN UPDATE tww_od.wwtp_structure wt SET fk_waste_water_treatment_plant = NEW.wt_fk_waste_water_treatment_plant , kind = NEW.wt_kind WHERE obj_id = OLD.obj_id;
WHEN NEW.ws_type = 'small_treatment_plant' THEN UPDATE tww_od.small_treatment_plant sm SET approval_number = NEW.sm_approval_number , function = NEW.sm_function , installation_number = NEW.sm_installation_number , remote_monitoring = NEW.sm_remote_monitoring WHERE obj_id = OLD.obj_id;
WHEN NEW.ws_type = 'drainless_toilet' THEN UPDATE tww_od.drainless_toilet dt SET kind = NEW.to_kind WHERE obj_id = OLD.obj_id;
ELSE -- do nothing END CASE;
-- Cover geometry has been moved IF NOT ST_Equals( OLD.situation3d_geometry, NEW.situation3d_geometry) THEN dx = ST_X(NEW.situation3d_geometry) - ST_X(OLD.situation3d_geometry); dy = ST_Y(NEW.situation3d_geometry) - ST_Y(OLD.situation3d_geometry);
-- Move wastewater node as well -- comment: TRANSLATE((ST_MakePoint(500, 900, 'NaN')), 10, 20, 0) would return NaN NaN NaN - so we have this workaround UPDATE tww_od.wastewater_node WN SET situation3d_geometry = ST_SetSRID( ST_MakePoint( ST_X(ST_TRANSLATE(ST_MakePoint(ST_X(WN.situation3d_geometry), ST_Y(WN.situation3d_geometry)), dx, dy )), ST_Y(ST_TRANSLATE(ST_MakePoint(ST_X(WN.situation3d_geometry), ST_Y(WN.situation3d_geometry)), dx, dy )), ST_Z(WN.situation3d_geometry)), 2056 ) WHERE obj_id IN ( SELECT obj_id FROM tww_od.wastewater_networkelement WHERE fk_wastewater_structure = NEW.obj_id );
-- Move covers UPDATE tww_od.cover CO SET situation3d_geometry = ST_SetSRID( ST_MakePoint( ST_X(ST_TRANSLATE(ST_MakePoint(ST_X(CO.situation3d_geometry), ST_Y(CO.situation3d_geometry)), dx, dy )), ST_Y(ST_TRANSLATE(ST_MakePoint(ST_X(CO.situation3d_geometry), ST_Y(CO.situation3d_geometry)), dx, dy )), ST_Z(CO.situation3d_geometry)), 2056 ) WHERE obj_id IN ( SELECT obj_id FROM tww_od.structure_part WHERE fk_wastewater_structure = NEW.obj_id );
-- Move reach(es) as well UPDATE tww_od.reach RE SET progression3d_geometry = ST_ForceCurve (ST_SetPoint( ST_CurveToLine (RE.progression3d_geometry ), 0, -- SetPoint index is 0 based, PointN index is 1 based. ST_SetSRID( ST_MakePoint( ST_X(ST_TRANSLATE(ST_MakePoint(ST_X(ST_PointN(RE.progression3d_geometry, 1)), ST_Y(ST_PointN(RE.progression3d_geometry, 1))), dx, dy )), ST_Y(ST_TRANSLATE(ST_MakePoint(ST_X(ST_PointN(RE.progression3d_geometry, 1)), ST_Y(ST_PointN(RE.progression3d_geometry, 1))), dx, dy )), ST_Z(ST_PointN(RE.progression3d_geometry, 1))), 2056 ) ) ) WHERE fk_reach_point_from IN ( SELECT RP.obj_id FROM tww_od.reach_point RP LEFT JOIN tww_od.wastewater_networkelement NE ON RP.fk_wastewater_networkelement = NE.obj_id WHERE NE.fk_wastewater_structure = NEW.obj_id );
UPDATE tww_od.reach RE SET progression3d_geometry = ST_ForceCurve( ST_SetPoint( ST_CurveToLine( RE.progression3d_geometry ), ST_NumPoints(RE.progression3d_geometry) - 1, ST_SetSRID( ST_MakePoint( ST_X(ST_TRANSLATE(ST_MakePoint(ST_X(ST_EndPoint(RE.progression3d_geometry)), ST_Y(ST_EndPoint(RE.progression3d_geometry))), dx, dy )), ST_Y(ST_TRANSLATE(ST_MakePoint(ST_X(ST_EndPoint(RE.progression3d_geometry)), ST_Y(ST_EndPoint(RE.progression3d_geometry))), dx, dy )), ST_Z(ST_PointN(RE.progression3d_geometry, 1))), 2056 ) ) ) WHERE fk_reach_point_to IN ( SELECT RP.obj_id FROM tww_od.reach_point RP LEFT JOIN tww_od.wastewater_networkelement NE ON RP.fk_wastewater_networkelement = NE.obj_id WHERE NE.fk_wastewater_structure = NEW.obj_id ); END IF;
RETURN NEW; END;

BEGIN
    CASE
        WHEN OLD.damage_type = 'channel'::tww_app.damage_type THEN DELETE FROM tww_od.damage_channel WHERE obj_id = OLD.obj_id;
      WHEN OLD.damage_type = 'manhole'::tww_app.damage_type THEN DELETE FROM tww_od.damage_manhole WHERE obj_id = OLD.obj_id;
    END CASE;
    DELETE FROM tww_od.damage WHERE obj_id = OLD.obj_id;
    RETURN NULL;
    END;

DECLARE
BEGIN
INSERT INTO tww_od.damage ( obj_id , comments , connection , fk_dataowner , fk_examination , fk_provider , last_modification , line_damage , single_damage_class , video_counter , view_parameters ) VALUES ( COALESCE( NEW.obj_id, tww_app.generate_oid('tww_od'::text, 'damage'::text) ) , NEW.comments , NEW.connection , NEW.fk_dataowner , NEW.fk_examination , NEW.fk_provider , NEW.last_modification , NEW.line_damage , NEW.single_damage_class , NEW.video_counter , NEW.view_parameters ) RETURNING obj_id INTO NEW.obj_id;
CASE WHEN NEW.damage_type = 'channel'::tww_app.damage_type THEN INSERT INTO tww_od.damage_channel ( obj_id , channel_damage_begin , channel_damage_code , channel_damage_end , channel_distance , channel_quantification1 , channel_quantification2 ) VALUES ( NEW.obj_id -- obj_id , NEW.channel_damage_begin , NEW.channel_damage_code , NEW.channel_damage_end , NEW.channel_distance , NEW.channel_quantification1 , NEW.channel_quantification2 );
WHEN NEW.damage_type = 'manhole'::tww_app.damage_type THEN INSERT INTO tww_od.damage_manhole ( obj_id , manhole_damage_begin , manhole_damage_code , manhole_damage_end , manhole_distance , manhole_quantification1 , manhole_quantification2 , manhole_shaft_area ) VALUES ( NEW.obj_id -- obj_id , NEW.manhole_damage_begin , NEW.manhole_damage_code , NEW.manhole_damage_end , NEW.manhole_distance , NEW.manhole_quantification1 , NEW.manhole_quantification2 , NEW.manhole_shaft_area );
ELSE NULL; END CASE;
RETURN NEW; END;

DECLARE
BEGIN
UPDATE tww_od.damage SET comments = NEW.comments , connection = NEW.connection , fk_dataowner = NEW.fk_dataowner , fk_examination = NEW.fk_examination , fk_provider = NEW.fk_provider , last_modification = NEW.last_modification , line_damage = NEW.line_damage , single_damage_class = NEW.single_damage_class , video_counter = NEW.video_counter , view_parameters = NEW.view_parameters WHERE obj_id = OLD.obj_id;
IF OLD.damage_type <> NEW.damage_type THEN RAISE EXCEPTION 'Type change not allowed for damage' USING HINT = 'You cannot switch from ' || OLD.damage_type || ' to ' || NEW.damage_type; END IF;
CASE WHEN NEW.damage_type = 'channel'::tww_app.damage_type THEN UPDATE tww_od.damage_channel channel SET obj_id = OLD.obj_id , channel_damage_begin = NEW.channel_damage_begin , channel_damage_code = NEW.channel_damage_code , channel_damage_end = NEW.channel_damage_end , channel_distance = NEW.channel_distance , channel_quantification1 = NEW.channel_quantification1 , channel_quantification2 = NEW.channel_quantification2 WHERE obj_id = OLD.obj_id; WHEN NEW.damage_type = 'manhole'::tww_app.damage_type THEN UPDATE tww_od.damage_manhole manhole SET obj_id = OLD.obj_id , manhole_damage_begin = NEW.manhole_damage_begin , manhole_damage_code = NEW.manhole_damage_code , manhole_damage_end = NEW.manhole_damage_end , manhole_distance = NEW.manhole_distance , manhole_quantification1 = NEW.manhole_quantification1 , manhole_quantification2 = NEW.manhole_quantification2 , manhole_shaft_area = NEW.manhole_shaft_area WHERE obj_id = OLD.obj_id; ELSE NULL; END CASE;
RETURN NEW; END;

