DECLARE
BEGIN
INSERT INTO tww_od.overflow ( obj_id , actuation , adjustability , brand , control , discharge_point , fk_control_center , fk_dataowner , fk_overflow_char , fk_overflow_to , fk_provider , fk_wastewater_node , function , gross_costs , identifier , last_modification , qon_dim , remark , signal_transmission , subsidies ) VALUES ( COALESCE( NEW.obj_id, tww_app.generate_oid('tww_od'::text, 'overflow'::text) ) , NEW.actuation , NEW.adjustability , NEW.brand , NEW.control , NEW.discharge_point , NEW.fk_control_center , NEW.fk_dataowner , NEW.fk_overflow_char , NEW.fk_overflow_to , NEW.fk_provider , NEW.fk_wastewater_node , NEW.function , NEW.gross_costs , NEW.identifier , NEW.last_modification , NEW.qon_dim , NEW.remark , NEW.signal_transmission , NEW.subsidies ) RETURNING obj_id INTO NEW.obj_id;
CASE WHEN NEW.overflow_type = 'leapingweir'::tww_app.overflow_type THEN INSERT INTO tww_od.leapingweir ( obj_id , length , opening_shape , width ) VALUES ( NEW.obj_id -- obj_id , NEW.length , NEW.opening_shape , NEW.width );
