WHEN NEW.overflow_type = 'pump'::tww_app.overflow_type THEN INSERT INTO tww_od.pump ( obj_id , construction_type , operating_point , placement_of_actuation , placement_of_pump , pump_flow_max_single , pump_flow_min_single , start_level , stop_level ) VALUES ( NEW.obj_id -- obj_id , NEW.construction_type , NEW.operating_point , NEW.placement_of_actuation , NEW.placement_of_pump , NEW.pump_flow_max_single , NEW.pump_flow_min_single , NEW.start_level , NEW.stop_level );
ELSE RAISE NOTICE 'vw_tww_overflow type not known (%)', NEW.overflow_type; -- ERROR END CASE;
