DECLARE
BEGIN
UPDATE tww_od.overflow SET actuation = NEW.actuation , adjustability = NEW.adjustability , brand = NEW.brand , control = NEW.control , discharge_point = NEW.discharge_point , fk_control_center = NEW.fk_control_center , fk_dataowner = NEW.fk_dataowner , fk_overflow_char = NEW.fk_overflow_char , fk_overflow_to = NEW.fk_overflow_to , fk_provider = NEW.fk_provider , fk_wastewater_node = NEW.fk_wastewater_node , function = NEW.function , gross_costs = NEW.gross_costs , identifier = NEW.identifier , last_modification = NEW.last_modification , qon_dim = NEW.qon_dim , remark = NEW.remark , signal_transmission = NEW.signal_transmission , subsidies = NEW.subsidies WHERE obj_id = OLD.obj_id;
IF OLD.overflow_type <> NEW.overflow_type THEN CASE WHEN OLD.overflow_type = 'leapingweir'::tww_app.overflow_type THEN DELETE FROM tww_od.leapingweir WHERE obj_id = OLD.obj_id; WHEN OLD.overflow_type = 'prank_weir'::tww_app.overflow_type THEN DELETE FROM tww_od.prank_weir WHERE obj_id = OLD.obj_id; WHEN OLD.overflow_type = 'pump'::tww_app.overflow_type THEN DELETE FROM tww_od.pump WHERE obj_id = OLD.obj_id; END CASE; CASE WHEN NEW.overflow_type = 'leapingweir'::tww_app.overflow_type THEN INSERT INTO tww_od.leapingweir (obj_id) VALUES (OLD.obj_id); WHEN NEW.overflow_type = 'prank_weir'::tww_app.overflow_type THEN INSERT INTO tww_od.prank_weir (obj_id) VALUES (OLD.obj_id); WHEN NEW.overflow_type = 'pump'::tww_app.overflow_type THEN INSERT INTO tww_od.pump (obj_id) VALUES (OLD.obj_id); ELSE -- do nothing END CASE; END IF;
CASE WHEN NEW.overflow_type = 'leapingweir'::tww_app.overflow_type THEN UPDATE tww_od.leapingweir leapingweir SET obj_id = OLD.obj_id , length = NEW.length , opening_shape = NEW.opening_shape , width = NEW.width WHERE obj_id = OLD.obj_id; WHEN NEW.overflow_type = 'prank_weir'::tww_app.overflow_type THEN UPDATE tww_od.prank_weir prank_weir SET obj_id = OLD.obj_id , hydraulic_overflow_length = NEW.hydraulic_overflow_length , level_max = NEW.level_max , level_min = NEW.level_min , weir_edge = NEW.weir_edge , weir_kind = NEW.weir_kind WHERE obj_id = OLD.obj_id; WHEN NEW.overflow_type = 'pump'::tww_app.overflow_type THEN UPDATE tww_od.pump pump SET obj_id = OLD.obj_id , construction_type = NEW.construction_type , operating_point = NEW.operating_point , placement_of_actuation = NEW.placement_of_actuation , placement_of_pump = NEW.placement_of_pump , pump_flow_max_single = NEW.pump_flow_max_single , pump_flow_min_single = NEW.pump_flow_min_single , start_level = NEW.start_level , stop_level = NEW.stop_level WHERE obj_id = OLD.obj_id; ELSE RAISE NOTICE 'vw_tww_overflow type not known (%)', NEW.overflow_type; -- ERROR END CASE;
RETURN NEW; END;

BEGIN -- Synchronize geometry with level NEW.progression3d_geometry = ST_ForceCurve(ST_SetPoint(ST_CurveToLine(NEW.progression3d_geometry),0, ST_MakePoint(ST_X(ST_StartPoint(NEW.progression3d_geometry)),ST_Y(ST_StartPoint(NEW.progression3d_geometry)),COALESCE(NEW.rp_from_level,'NaN'))));
NEW.progression3d_geometry = ST_ForceCurve(ST_SetPoint(ST_CurveToLine(NEW.progression3d_geometry),ST_NumPoints(NEW.progression3d_geometry)-1, ST_MakePoint(ST_X(ST_EndPoint(NEW.progression3d_geometry)),ST_Y(ST_EndPoint(NEW.progression3d_geometry)),COALESCE(NEW.rp_to_level,'NaN'))));
INSERT INTO tww_od.reach_point ( obj_id , elevation_accuracy , fk_dataowner , fk_provider , fk_wastewater_networkelement , identifier , last_modification , level , outlet_shape , pipe_closure , position_of_connection , remark , situation3d_geometry ) VALUES ( COALESCE( NEW.rp_from_obj_id, tww_app.generate_oid('tww_od'::text, 'reach_point'::text) ) , NEW.rp_from_elevation_accuracy , COALESCE(NULLIF(NEW.rp_from_fk_dataowner,''), NEW.fk_dataowner) -- fk_dataowner , COALESCE(NULLIF(NEW.rp_from_fk_provider,''), NEW.fk_provider) -- fk_provider , NEW.rp_from_fk_wastewater_networkelement , NEW.rp_from_identifier , NEW.rp_from_last_modification , NEW.rp_from_level , NEW.rp_from_outlet_shape , NEW.rp_from_pipe_closure , NEW.rp_from_position_of_connection , NEW.rp_from_remark , ST_StartPoint(NEW.progression3d_geometry) -- situation3d_geometry ) RETURNING obj_id INTO NEW.rp_from_obj_id;
INSERT INTO tww_od.reach_point ( obj_id , elevation_accuracy , fk_dataowner , fk_provider , fk_wastewater_networkelement , identifier , last_modification , level , outlet_shape , pipe_closure , position_of_connection , remark , situation3d_geometry ) VALUES ( COALESCE( NEW.rp_to_obj_id, tww_app.generate_oid('tww_od'::text, 'reach_point'::text) ) , NEW.rp_to_elevation_accuracy , COALESCE(NULLIF(NEW.rp_to_fk_dataowner,''), NEW.fk_dataowner) -- fk_dataowner , COALESCE(NULLIF(NEW.rp_to_fk_provider,''), NEW.fk_provider) -- fk_provider , NEW.rp_to_fk_wastewater_networkelement , NEW.rp_to_identifier , NEW.rp_to_last_modification , NEW.rp_to_level , NEW.rp_to_outlet_shape , NEW.rp_to_pipe_closure , NEW.rp_to_position_of_connection , NEW.rp_to_remark , ST_EndPoint(NEW.progression3d_geometry) -- situation3d_geometry ) RETURNING obj_id INTO NEW.rp_to_obj_id;
INSERT INTO tww_od.wastewater_structure ( obj_id , _bottom_label , _cover_label -- , _depth , _input_label -- , _label , _output_label , accessibility , condition_score , contract_section -- , detail_geometry3d_geometry , elevation_determination , financing , fk_dataowner -- , fk_main_cover , fk_main_wastewater_node , fk_operator , fk_owner , fk_provider , gross_costs , identifier , inspection_interval , last_modification , location_name , records , remark , renovation_necessity , replacement_value , rv_base_year , rv_construction_type , status , status_survey_year , structure_condition , subsidies , urgency_figure , year_of_construction , year_of_replacement ) VALUES ( NEW.ws_obj_id , NEW.ws__bottom_label , NEW.ws__cover_label -- , NEW.ws__depth , NEW.ws__input_label -- , NEW.ws__label , NEW.ws__output_label , NEW.ws_accessibility , NEW.ws_condition_score , NEW.ws_contract_section -- , NEW.ws_detail_geometry3d_geometry , NEW.ws_elevation_determination , NEW.ws_financing , NEW.fk_dataowner -- fk_dataowner -- , NEW.ws_fk_main_cover , NEW.ws_fk_main_wastewater_node , NEW.ws_fk_operator , NEW.ws_fk_owner , NEW.fk_provider -- fk_provider , NEW.ws_gross_costs , NEW.ws_identifier , NEW.ws_inspection_interval , NEW.ws_last_modification , NEW.ws_location_name , NEW.ws_records , NEW.ws_remark , NEW.ws_renovation_necessity , NEW.ws_replacement_value , NEW.ws_rv_base_year , NEW.ws_rv_construction_type , NEW.ws_status , NEW.ws_status_survey_year , NEW.ws_structure_condition , NEW.ws_subsidies , NEW.ws_urgency_figure , NEW.ws_year_of_construction , NEW.ws_year_of_replacement );
