INSERT INTO tww_od.reach ( obj_id , clear_height , coefficient_of_friction , elevation_determination , fk_pipe_profile , fk_reach_point_from , fk_reach_point_to , flow_time_dry_weather , horizontal_positioning , hydraulic_load_current , inside_coating , leak_protection , length_effective , material , progression3d_geometry , reliner_material , reliner_nominal_size , relining_construction , relining_kind , ring_stiffness , slope_building_plan , swmm_default_coefficient_of_friction , wall_roughness ) VALUES ( NEW.obj_id , NEW.clear_height , NEW.coefficient_of_friction , NEW.elevation_determination , NEW.fk_pipe_profile , NEW.rp_from_obj_id -- fk_reach_point_from , NEW.rp_to_obj_id -- fk_reach_point_to , NEW.flow_time_dry_weather , NEW.horizontal_positioning , NEW.hydraulic_load_current , NEW.inside_coating , NEW.leak_protection , NEW.length_effective , NEW.material , NEW.progression3d_geometry , NEW.reliner_material , NEW.reliner_nominal_size , NEW.relining_construction , NEW.relining_kind , NEW.ring_stiffness , NEW.slope_building_plan , NEW.swmm_default_coefficient_of_friction , NEW.wall_roughness );
