DECLARE dx float; dy float; BEGIN UPDATE tww_od.cover co SET brand = NEW.co_brand , cover_shape = NEW.co_shape , diameter = NEW.co_diameter , fastening = NEW.co_fastening , level = NEW.co_level , material = NEW.co_material , positional_accuracy = NEW.co_positional_accuracy -- , situation3d_geometry = NEW.co_situation3d_geometry , sludge_bucket = NEW.co_sludge_bucket , venting = NEW.co_venting WHERE obj_id = OLD.co_obj_id; UPDATE tww_od.structure_part sp SET fk_dataowner = NEW.fk_dataowner , fk_provider = NEW.fk_provider -- , fk_wastewater_structure = NEW.co_fk_wastewater_structure , identifier = NEW.co_identifier , last_modification = NEW.last_modification , remark = NEW.co_remark , renovation_demand = NEW.co_renovation_demand WHERE obj_id = OLD.co_obj_id; UPDATE tww_od.wastewater_structure ws SET obj_id = NEW.obj_id -- , _bottom_label = NEW._bottom_label -- , _cover_label = NEW._cover_label -- , _depth = NEW._depth -- , _input_label = NEW._input_label -- , _label = NEW._label -- , _output_label = NEW._output_label , accessibility = NEW.accessibility , condition_score = NEW.condition_score , contract_section = NEW.contract_section -- , detail_geometry3d_geometry = NEW.detail_geometry3d_geometry , elevation_determination = NEW.elevation_determination , financing = NEW.financing , fk_dataowner = NEW.fk_dataowner -- , fk_main_cover = NEW.fk_main_cover -- , fk_main_wastewater_node = NEW.fk_main_wastewater_node , fk_operator = NEW.fk_operator , fk_owner = NEW.fk_owner , fk_provider = NEW.fk_provider , gross_costs = NEW.gross_costs , identifier = NEW.identifier , inspection_interval = NEW.inspection_interval -- , last_modification = NEW.last_modification , location_name = NEW.location_name , records = NEW.records , remark = NEW.remark , renovation_necessity = NEW.renovation_necessity , replacement_value = NEW.replacement_value , rv_base_year = NEW.rv_base_year , rv_construction_type = NEW.rv_construction_type , status = NEW.status , status_survey_year = NEW.status_survey_year , structure_condition = NEW.structure_condition , subsidies = NEW.subsidies , urgency_figure = NEW.urgency_figure , year_of_construction = NEW.year_of_construction , year_of_replacement = NEW.year_of_replacement WHERE obj_id = OLD.obj_id; UPDATE tww_od.wastewater_node wn SET -- _function_hierarchic = NEW.wn__function_hierarchic -- _status = NEW.wn__status -- _usage_current = NEW.wn__usage_current backflow_level_current = NEW.wn_backflow_level_current , bottom_level = NEW.wn_bottom_level , elevation_accuracy = NEW.wn_elevation_accuracy , fk_hydr_geometry = NEW.wn_fk_hydr_geometry , function_node_amelioration = NEW.wn_function_node_amelioration -- , situation3d_geometry = NEW.wn_situation3d_geometry , wwtp_number = NEW.wn_wwtp_number WHERE obj_id = OLD.wn_obj_id; UPDATE tww_od.wastewater_networkelement ne SET fk_dataowner = NEW.wn_fk_dataowner , fk_provider = NEW.wn_fk_provider , fk_wastewater_structure = NEW.wn_fk_wastewater_structure , identifier = NEW.wn_identifier , last_modification = NEW.wn_last_modification , remark = NEW.wn_remark WHERE obj_id = OLD.wn_obj_id;
IF OLD.ws_type <> NEW.ws_type THEN CASE WHEN OLD.ws_type <> 'unknown' THEN BEGIN EXECUTE FORMAT('DELETE FROM tww_od.%I WHERE obj_id = %L',OLD.ws_type,OLD.obj_id); END; END CASE;
CASE WHEN NEW.ws_type = ANY(ARRAY['manhole','special_structure','discharge_point','infiltration_installation','drainless_toilet','wwtp_structure','small_treatment_plant']) THEN BEGIN EXECUTE FORMAT('INSERT INTO tww_od.%I(obj_id) VALUES (%L)',NEW.ws_type,OLD.obj_id); END; END CASE; END IF;
CASE WHEN NEW.ws_type = 'manhole' THEN UPDATE tww_od.manhole ws SET -- _orientation = NEW.ma__orientation amphibian_exit = NEW.ma_amphibian_exit , dimension1 = NEW.ma_dimension1 , dimension2 = NEW.ma_dimension2 , function = NEW.ma_function , material = NEW.ma_material , possibility_intervention = NEW.ma_possibility_intervention , surface_inflow = NEW.ma_surface_inflow WHERE obj_id = OLD.obj_id;
WHEN NEW.ws_type = 'special_structure' THEN UPDATE tww_od.special_structure ss SET amphibian_exit = NEW.ss_amphibian_exit , bypass = NEW.ss_bypass , emergency_overflow = NEW.ss_emergency_overflow , function = NEW.ss_function , possibility_intervention = NEW.ss_possibility_intervention , stormwater_tank_arrangement = NEW.ss_stormwater_tank_arrangement , upper_elevation = NEW.ss_upper_elevation WHERE obj_id = OLD.obj_id;
WHEN NEW.ws_type = 'discharge_point' THEN UPDATE tww_od.discharge_point dp SET highwater_level = NEW.dp_highwater_level , relevance = NEW.dp_relevance , terrain_level = NEW.dp_terrain_level , upper_elevation = NEW.dp_upper_elevation , water_course_number = NEW.dp_water_course_number , water_course_segment_canton = NEW.dp_water_course_segment_canton , waterlevel_hydraulic = NEW.dp_waterlevel_hydraulic WHERE obj_id = OLD.obj_id;
WHEN NEW.ws_type = 'infiltration_installation' THEN UPDATE tww_od.infiltration_installation ii SET absorption_capacity = NEW.ii_absorption_capacity , defects = NEW.ii_defects , dimension1 = NEW.ii_dimension1 , dimension2 = NEW.ii_dimension2 , distance_to_aquifer = NEW.ii_distance_to_aquifer , effective_area = NEW.ii_effective_area , emergency_overflow = NEW.ii_emergency_overflow , filling_material = NEW.ii_filling_material , fk_dss15_aquifer = NEW.ii_fk_dss15_aquifer , kind = NEW.ii_kind , labeling = NEW.ii_labeling , seepage_utilization = NEW.ii_seepage_utilization , upper_elevation = NEW.ii_upper_elevation , vehicle_access = NEW.ii_vehicle_access , watertightness = NEW.ii_watertightness WHERE obj_id = OLD.obj_id;
ELSE -- do nothing END CASE;
-- Cover geometry has been moved IF NOT ST_Equals( OLD.situation3d_geometry, NEW.situation3d_geometry) THEN dx = ST_X(NEW.situation3d_geometry) - ST_X(OLD.situation3d_geometry); dy = ST_Y(NEW.situation3d_geometry) - ST_Y(OLD.situation3d_geometry);
-- Move wastewater node as well -- comment: TRANSLATE((ST_MakePoint(500, 900, 'NaN')), 10, 20, 0) would return NaN NaN NaN - so we have this workaround UPDATE tww_od.wastewater_node WN SET situation3d_geometry = ST_SetSRID( ST_MakePoint( ST_X(ST_TRANSLATE(ST_MakePoint(ST_X(WN.situation3d_geometry), ST_Y(WN.situation3d_geometry)), dx, dy )), ST_Y(ST_TRANSLATE(ST_MakePoint(ST_X(WN.situation3d_geometry), ST_Y(WN.situation3d_geometry)), dx, dy )), ST_Z(WN.situation3d_geometry)), 2056 ) WHERE obj_id IN ( SELECT obj_id FROM tww_od.wastewater_networkelement WHERE fk_wastewater_structure = NEW.obj_id );
-- Move reach point node as well UPDATE tww_od.reach_point RP SET situation3d_geometry = ST_SetSRID( ST_MakePoint( ST_X(ST_TRANSLATE(ST_MakePoint(ST_X(RP.situation3d_geometry), ST_Y(RP.situation3d_geometry)), dx, dy )), ST_Y(ST_TRANSLATE(ST_MakePoint(ST_X(RP.situation3d_geometry), ST_Y(RP.situation3d_geometry)), dx, dy )), ST_Z(RP.situation3d_geometry)), 2056 ) WHERE obj_id IN ( SELECT RP.obj_id FROM tww_od.reach_point RP LEFT JOIN tww_od.wastewater_networkelement NE ON RP.fk_wastewater_networkelement = NE.obj_id WHERE NE.fk_wastewater_structure = NEW.obj_id );
-- Move covers UPDATE tww_od.cover CO SET situation3d_geometry = ST_SetSRID( ST_MakePoint( ST_X(ST_TRANSLATE(ST_MakePoint(ST_X(CO.situation3d_geometry), ST_Y(CO.situation3d_geometry)), dx, dy )), ST_Y(ST_TRANSLATE(ST_MakePoint(ST_X(CO.situation3d_geometry), ST_Y(CO.situation3d_geometry)), dx, dy )), ST_Z(CO.situation3d_geometry)), 2056 ) WHERE obj_id IN ( SELECT obj_id FROM tww_od.structure_part WHERE fk_wastewater_structure = NEW.obj_id );
-- Move reach(es) as well UPDATE tww_od.reach RE SET progression3d_geometry = ST_ForceCurve (ST_SetPoint( ST_CurveToLine (RE.progression3d_geometry ), 0, -- SetPoint index is 0 based, PointN index is 1 based. ST_SetSRID( ST_MakePoint( ST_X(ST_TRANSLATE(ST_MakePoint(ST_X(ST_PointN(RE.progression3d_geometry, 1)), ST_Y(ST_PointN(RE.progression3d_geometry, 1))), dx, dy )), ST_Y(ST_TRANSLATE(ST_MakePoint(ST_X(ST_PointN(RE.progression3d_geometry, 1)), ST_Y(ST_PointN(RE.progression3d_geometry, 1))), dx, dy )), ST_Z(ST_PointN(RE.progression3d_geometry, 1))), 2056 ) ) ) WHERE fk_reach_point_from IN ( SELECT RP.obj_id FROM tww_od.reach_point RP LEFT JOIN tww_od.wastewater_networkelement NE ON RP.fk_wastewater_networkelement = NE.obj_id WHERE NE.fk_wastewater_structure = NEW.obj_id );
UPDATE tww_od.reach RE SET progression3d_geometry = ST_ForceCurve( ST_SetPoint( ST_CurveToLine( RE.progression3d_geometry ), ST_NumPoints(RE.progression3d_geometry) - 1, ST_SetSRID( ST_MakePoint( ST_X(ST_TRANSLATE(ST_MakePoint(ST_X(ST_EndPoint(RE.progression3d_geometry)), ST_Y(ST_EndPoint(RE.progression3d_geometry))), dx, dy )), ST_Y(ST_TRANSLATE(ST_MakePoint(ST_X(ST_EndPoint(RE.progression3d_geometry)), ST_Y(ST_EndPoint(RE.progression3d_geometry))), dx, dy )), ST_Z(ST_PointN(RE.progression3d_geometry, 1))), 2056 ) ) ) WHERE fk_reach_point_to IN ( SELECT RP.obj_id FROM tww_od.reach_point RP LEFT JOIN tww_od.wastewater_networkelement NE ON RP.fk_wastewater_networkelement = NE.obj_id WHERE NE.fk_wastewater_structure = NEW.obj_id ); END IF;
RETURN NEW; END;

BEGIN
  DELETE FROM tww_od.wastewater_node WHERE obj_id = OLD.obj_id;
  DELETE FROM tww_od.wastewater_networkelement WHERE obj_id = OLD.obj_id;
RETURN NULL;
END;

BEGIN INSERT INTO tww_od.wastewater_networkelement ( obj_id , fk_dataowner , fk_provider , fk_wastewater_structure , identifier , last_modification , remark ) VALUES ( COALESCE( NEW.obj_id, tww_app.generate_oid('tww_od'::text, 'wastewater_node'::text) ) , NEW.fk_dataowner , NEW.fk_provider , NEW.fk_wastewater_structure , CASE WHEN NEW.identifier IS NOT NULL AND NEW.identifier::text <> '' THEN NEW.identifier ELSE COALESCE( NEW.obj_id, tww_app.generate_oid('tww_od'::text, 'wastewater_node'::text) ) END , NEW.last_modification , NEW.remark ) RETURNING obj_id INTO NEW.obj_id;
INSERT INTO tww_od.wastewater_node ( obj_id , _function_hierarchic , _status , _usage_current , backflow_level_current , bottom_level , elevation_accuracy , fk_hydr_geometry , function_node_amelioration , situation3d_geometry , wwtp_number ) VALUES ( NEW.obj_id , NEW._function_hierarchic , NEW._status , NEW._usage_current , NEW.backflow_level_current , NEW.bottom_level , NEW.elevation_accuracy , NEW.fk_hydr_geometry , NEW.function_node_amelioration , NEW.situation3d_geometry , NEW.wwtp_number );
RETURN NEW; END;

DECLARE
    BEGIN
      DELETE FROM tww_od.wastewater_structure WHERE obj_id = OLD.obj_id;
    RETURN OLD;
    END;

BEGIN
NEW.identifier = COALESCE(NEW.identifier, NEW.obj_id);
INSERT INTO tww_od.wastewater_structure ( obj_id -- , _bottom_label -- , _cover_label , _depth -- , _input_label -- , _label -- , _output_label , accessibility , condition_score , contract_section , detail_geometry3d_geometry , elevation_determination , financing , fk_dataowner , fk_main_cover , fk_main_wastewater_node , fk_operator , fk_owner , fk_provider , gross_costs , identifier , inspection_interval , last_modification , location_name , records , remark , renovation_necessity , replacement_value , rv_base_year , rv_construction_type , status , status_survey_year , structure_condition , subsidies , urgency_figure , year_of_construction , year_of_replacement ) VALUES ( NEW.obj_id -- , NEW._bottom_label -- , NEW._cover_label , NEW._depth -- , NEW._input_label -- , NEW._label -- , NEW._output_label , NEW.accessibility , NEW.condition_score , NEW.contract_section , NEW.detail_geometry3d_geometry , NEW.elevation_determination , NEW.financing , NEW.fk_dataowner , NEW.fk_main_cover , NEW.fk_main_wastewater_node , NEW.fk_operator , NEW.fk_owner , NEW.fk_provider , NEW.gross_costs , NEW.identifier , NEW.inspection_interval , NEW.last_modification , NEW.location_name , NEW.records , NEW.remark , NEW.renovation_necessity , NEW.replacement_value , NEW.rv_base_year , NEW.rv_construction_type , NEW.status , NEW.status_survey_year , NEW.structure_condition , NEW.subsidies , NEW.urgency_figure , NEW.year_of_construction , NEW.year_of_replacement );
RETURN NEW; END;

BEGIN
  DELETE FROM tww_od.wwtp_structure WHERE obj_id = OLD.obj_id;
  DELETE FROM tww_od.wastewater_structure WHERE obj_id = OLD.obj_id;
RETURN NULL;
END;

BEGIN INSERT INTO tww_od.wastewater_structure ( obj_id , _bottom_label , _cover_label , _depth , _input_label , _label , _output_label , accessibility , condition_score , contract_section , detail_geometry3d_geometry , elevation_determination , financing , fk_dataowner , fk_main_cover , fk_main_wastewater_node , fk_operator , fk_owner , fk_provider , gross_costs , identifier , inspection_interval , last_modification , location_name , records , remark , renovation_necessity , replacement_value , rv_base_year , rv_construction_type , status , status_survey_year , structure_condition , subsidies , urgency_figure , year_of_construction , year_of_replacement ) VALUES ( COALESCE( NEW.obj_id, tww_app.generate_oid('tww_od'::text, 'wwtp_structure'::text) ) , NEW._bottom_label , NEW._cover_label , NEW._depth , NEW._input_label , NEW._label , NEW._output_label , NEW.accessibility , NEW.condition_score , NEW.contract_section , NEW.detail_geometry3d_geometry , NEW.elevation_determination , NEW.financing , NEW.fk_dataowner , NEW.fk_main_cover , NEW.fk_main_wastewater_node , NEW.fk_operator , NEW.fk_owner , NEW.fk_provider , NEW.gross_costs , CASE WHEN NEW.identifier IS NOT NULL AND NEW.identifier::text <> '' THEN NEW.identifier ELSE COALESCE( NEW.obj_id, tww_app.generate_oid('tww_od'::text, 'wwtp_structure'::text) ) END , NEW.inspection_interval , NEW.last_modification , NEW.location_name , NEW.records , NEW.remark , NEW.renovation_necessity , NEW.replacement_value , NEW.rv_base_year , NEW.rv_construction_type , NEW.status , NEW.status_survey_year , NEW.structure_condition , NEW.subsidies , NEW.urgency_figure , NEW.year_of_construction , NEW.year_of_replacement ) RETURNING obj_id INTO NEW.obj_id;
INSERT INTO tww_od.wwtp_structure ( obj_id , fk_waste_water_treatment_plant , kind ) VALUES ( NEW.obj_id , NEW.fk_waste_water_treatment_plant , NEW.kind );
RETURN NEW; END;

DECLARE
  myrec_prefix record;
  myrec_shortcut record;
  myrec_seq record;
BEGIN
  -- first we have to get the OID prefix
  BEGIN
    SELECT prefix::text INTO myrec_prefix FROM tww_sys.oid_prefixes WHERE active = TRUE;
    EXCEPTION
        WHEN NO_DATA_FOUND THEN
           RAISE EXCEPTION 'no active record found in table tww_sys.oid_prefixes';
        WHEN TOO_MANY_ROWS THEN
	   RAISE EXCEPTION 'more than one active records found in table tww_sys.oid_prefixes';
  END;
  -- test if prefix is of correct length
  IF char_length(myrec_prefix.prefix) != 8 THEN
    RAISE EXCEPTION 'character length of prefix must be 8';
  END IF;
  --get table 2char shortcut
  BEGIN
    SELECT shortcut_en INTO STRICT myrec_shortcut FROM tww_sys.dictionary_od_table WHERE tablename = table_name;
    EXCEPTION
        WHEN NO_DATA_FOUND THEN
            RAISE EXCEPTION 'dictionary entry for table % not found', table_name;
        WHEN TOO_MANY_ROWS THEN
            RAISE EXCEPTION 'dictonary entry for table % not unique', table_name;
  END;
  --get sequence for table
  EXECUTE format('SELECT nextval(''%1$I.seq_%2$I_oid'') AS seqval', schema_name, table_name) INTO myrec_seq;
  IF NOT FOUND THEN
    RAISE EXCEPTION 'sequence for table % not found', table_name;
  END IF;
  RETURN myrec_prefix.prefix || myrec_shortcut.shortcut_en || to_char(myrec_seq.seqval,'FM000000');
END;

DECLARE
    myrec record;
BEGIN
  BEGIN
    SELECT value_obj_id::varchar(16) INTO myrec FROM tww_od.default_values WHERE fieldname = field_name;
    EXCEPTION
        WHEN NO_DATA_FOUND THEN
           RAISE WARNING 'Field name % not found in table tww_od.default_values. Returning NULL',field_name;
		   RETURN NULL;
  END;
  RETURN myrec.value_obj_id;
END;

BEGIN
  DELETE FROM tww_od.import_manhole_quarantine
  WHERE quarantine_serial = NEW.quarantine_serial;
  RAISE NOTICE 'Deleted row in tww_od.import_manhole_quarantine';
  RETURN NEW;
END;

DECLARE let_kind text; new_lets integer; old_lets integer; BEGIN let_kind := TG_ARGV[0];
-- count new lets IF let_kind='inlet' AND ( NEW.inlet_3_material IS NOT NULL OR NEW.inlet_3_depth_m IS NOT NULL OR NEW.inlet_3_clear_height IS NOT NULL ) OR let_kind='outlet' AND ( NEW.outlet_1_material IS NOT NULL OR NEW.outlet_1_depth_m IS NOT NULL OR NEW.outlet_1_clear_height IS NOT NULL ) THEN IF let_kind='inlet' AND ( NEW.inlet_4_material IS NOT NULL OR NEW.inlet_4_depth_m IS NOT NULL OR NEW.inlet_4_clear_height IS NOT NULL ) OR let_kind='outlet' AND ( NEW.outlet_2_material IS NOT NULL OR NEW.outlet_2_depth_m IS NOT NULL OR NEW.outlet_2_clear_height IS NOT NULL ) THEN new_lets = 2; -- it's possibly more, but at least > 1 ELSE new_lets = 1; END IF; ELSE new_lets = 0; END IF; -- count old lets old_lets = ( SELECT COUNT (*) FROM tww_od.reach re LEFT JOIN tww_od.reach_point rp ON let_kind='inlet' AND rp.obj_id = re.fk_reach_point_to OR let_kind='outlet' AND rp.obj_id = re.fk_reach_point_from LEFT JOIN tww_od.wastewater_networkelement wn ON wn.obj_id = rp.fk_wastewater_networkelement LEFT JOIN tww_app.vw_tww_wastewater_structure ws ON ws.obj_id = wn.fk_wastewater_structure WHERE ws.obj_id = NEW.obj_id );
-- handle inlets IF ( new_lets > 1 AND old_lets > 0 ) OR old_lets > 1 THEN -- request for update because new lets are bigger 1 (and old lets not 0 ) or old lets are bigger 1 RAISE NOTICE 'Impossible to assign %s - manual edit needed.', let_kind; ELSE IF new_lets = 0 AND old_lets > 0 THEN -- request for delete because no new lets but old lets RAISE NOTICE 'No new %s but old ones - manual delete needed.', let_kind; ELSIF new_lets > 0 AND old_lets = 0 THEN -- request for create because no old lets but new lets RAISE NOTICE 'No old %s but new ones - manual create needed.', let_kind; ELSE IF new_lets = 1 AND old_lets = 1 THEN IF let_kind='inlet' THEN -- update material and dimension on reach UPDATE tww_od.reach SET material = NEW.inlet_3_material, clear_height = NEW.inlet_3_clear_height WHERE obj_id = ( SELECT re.obj_id FROM tww_od.reach re LEFT JOIN tww_od.reach_point rp ON rp.obj_id = re.fk_reach_point_to LEFT JOIN tww_od.wastewater_networkelement wn ON wn.obj_id = rp.fk_wastewater_networkelement LEFT JOIN tww_app.vw_tww_wastewater_structure ws ON ws.obj_id = wn.fk_wastewater_structure WHERE ws.obj_id = NEW.obj_id );
-- update depth_m on reach_point UPDATE tww_od.reach_point SET level = NEW.co_level - NEW.inlet_3_depth_m WHERE obj_id = ( SELECT rp.obj_id FROM tww_od.reach re LEFT JOIN tww_od.reach_point rp ON rp.obj_id = re.fk_reach_point_to LEFT JOIN tww_od.wastewater_networkelement wn ON wn.obj_id = rp.fk_wastewater_networkelement LEFT JOIN tww_app.vw_tww_wastewater_structure ws ON ws.obj_id = wn.fk_wastewater_structure WHERE ws.obj_id = NEW.obj_id ); ELSE -- update material on reach UPDATE tww_od.reach SET material = NEW.outlet_1_material, clear_height = NEW.outlet_1_clear_height WHERE obj_id = ( SELECT re.obj_id FROM tww_od.reach re LEFT JOIN tww_od.reach_point rp ON rp.obj_id = re.fk_reach_point_from LEFT JOIN tww_od.wastewater_networkelement wn ON wn.obj_id = rp.fk_wastewater_networkelement LEFT JOIN tww_app.vw_tww_wastewater_structure ws ON ws.obj_id = wn.fk_wastewater_structure WHERE ws.obj_id = NEW.obj_id );
-- update depth_m on reach_point UPDATE tww_od.reach_point SET level = NEW.co_level - NEW.outlet_1_depth_m WHERE obj_id = ( SELECT rp.obj_id FROM tww_od.reach re LEFT JOIN tww_od.reach_point rp ON rp.obj_id = re.fk_reach_point_from LEFT JOIN tww_od.wastewater_networkelement wn ON wn.obj_id = rp.fk_wastewater_networkelement LEFT JOIN tww_app.vw_tww_wastewater_structure ws ON ws.obj_id = wn.fk_wastewater_structure WHERE ws.obj_id = NEW.obj_id ); END IF;
RAISE NOTICE '%s updated', let_kind; ELSE -- do nothing RAISE NOTICE 'No %s - nothing to do', let_kind; END IF;
IF let_kind='inlet' THEN -- set inlet okay UPDATE tww_od.import_manhole_quarantine SET inlet_okay = true WHERE quarantine_serial = NEW.quarantine_serial; ELSE -- set outlet okay UPDATE tww_od.import_manhole_quarantine SET outlet_okay = true WHERE quarantine_serial = NEW.quarantine_serial; END IF;
END IF; END IF; RETURN NEW;
-- catch EXCEPTION WHEN OTHERS THEN RAISE NOTICE 'EXCEPTION: %', SQLERRM; RETURN NEW; END;

BEGIN
-- in case there is a depth, but no refercing value - it should stay in quarantene IF( NEW._depth IS NOT NULL AND NEW.co_level IS NULL AND NEW.wn_bottom_level IS NULL ) THEN RAISE EXCEPTION 'No referencing value for calculation with depth'; END IF;
-- tww_od.wastewater_structure IF( SELECT TRUE FROM tww_app.vw_tww_wastewater_structure WHERE obj_id = NEW.obj_id ) THEN UPDATE tww_app.vw_tww_wastewater_structure SET identifier = NEW.identifier, situation_geometry = ST_Force2D(NEW.situation_geometry), co_shape = NEW.co_shape, co_diameter = NEW.co_diameter, co_material = NEW.co_material, co_positional_accuracy = NEW.co_positional_accuracy, co_level = (CASE WHEN NEW.co_level IS NULL AND NEW.wn_bottom_level IS NOT NULL AND NEW._depth IS NOT NULL THEN NEW.wn_bottom_level + NEW._depth ELSE NEW.co_level END), _depth = NEW._depth, _channel_usage_current = NEW._channel_usage_current, ma_material = NEW.ma_material, ma_dimension1 = NEW.ma_dimension1, ma_dimension2 = NEW.ma_dimension2, ws_type = NEW.ws_type, ma_function = NEW.ma_function, ss_function = NEW.ss_function, remark = NEW.remark, wn_bottom_level = (CASE WHEN NEW.wn_bottom_level IS NULL AND NEW.co_level IS NOT NULL AND NEW._depth IS NOT NULL THEN NEW.co_level - NEW._depth ELSE NEW.wn_bottom_level END) WHERE obj_id = NEW.obj_id; RAISE NOTICE 'Updated row in tww_app.vw_tww_wastewater_structure'; ELSE INSERT INTO tww_app.vw_tww_wastewater_structure ( obj_id, identifier, situation_geometry, co_shape, co_diameter, co_material, co_positional_accuracy, co_level, _depth, _channel_usage_current, ma_material, ma_dimension1, ma_dimension2, ws_type, ma_function, ss_function, remark, wn_bottom_level ) VALUES ( NEW.obj_id, NEW.identifier, ST_Force2D(NEW.situation_geometry), NEW.co_shape, NEW.co_diameter, NEW.co_material, NEW.co_positional_accuracy, (CASE WHEN NEW.co_level IS NULL AND NEW.wn_bottom_level IS NOT NULL AND NEW._depth IS NOT NULL THEN NEW.wn_bottom_level + NEW._depth ELSE NEW.co_level END), NEW._depth, NEW._channel_usage_current, NEW.ma_material, NEW.ma_dimension1, NEW.ma_dimension2, NEW.ws_type, NEW.ma_function, NEW.ss_function, NEW.remark, (CASE WHEN NEW.wn_bottom_level IS NULL AND NEW.co_level IS NOT NULL AND NEW._depth IS NOT NULL THEN NEW.co_level - NEW._depth ELSE NEW.wn_bottom_level END) ); RAISE NOTICE 'Inserted row in tww_app.vw_tww_wastewater_structure'; END IF;
-- photo1 insert IF (NEW.photo1 IS NOT NULL) THEN INSERT INTO tww_od.file ( object, identifier ) VALUES ( NEW.obj_id, NEW.photo1 ); RAISE NOTICE 'Inserted row in tww_od.file'; END IF;
-- photo2 insert IF (NEW.photo2 IS NOT NULL) THEN INSERT INTO tww_od.file ( object, identifier ) VALUES ( NEW.obj_id, NEW.photo2 ); RAISE NOTICE 'Inserted row in tww_od.file'; END IF;
-- set structure okay UPDATE tww_od.import_manhole_quarantine SET structure_okay = true WHERE quarantine_serial = NEW.quarantine_serial; RETURN NEW;
-- catch EXCEPTION WHEN OTHERS THEN RAISE NOTICE 'EXCEPTION: %', SQLERRM; RETURN NEW; END;

BEGIN
 NEW.last_modification := TIMEOFDAY();

 RETURN NEW;
END;

DECLARE
 table_name TEXT;
BEGIN
 table_name = TG_ARGV[0];

 EXECUTE '
 UPDATE ' || table_name || '
 SET last_modification = TIMEOFDAY()::timestamp
 WHERE obj_id = ''' || NEW.obj_id || '''
';
 RETURN NEW;
END;

DECLARE
  channel_id text;
  reach_count integer;
BEGIN
  -- get channel obj_id
  SELECT fk_wastewater_structure INTO channel_id
    FROM tww_od.wastewater_networkelement
    WHERE wastewater_networkelement.obj_id = OLD.obj_id;

  DELETE FROM tww_od.wastewater_networkelement WHERE obj_id = OLD.obj_id;
  DELETE FROM tww_od.reach_point WHERE obj_id = OLD.fk_reach_point_from;
  DELETE FROM tww_od.reach_point WHERE obj_id = OLD.fk_reach_point_to;

  -- delete channel if no reach left
  SELECT COUNT(fk_wastewater_structure) INTO reach_count
    FROM tww_od.wastewater_networkelement
    WHERE fk_wastewater_structure = channel_id;
  IF reach_count = 0 THEN
    RAISE NOTICE 'Removing channel (%) since no reach is left', channel_id;
    DELETE FROM tww_od.channel WHERE obj_id = channel_id;
    DELETE FROM tww_od.wastewater_structure WHERE obj_id = channel_id;
  END IF;
  RETURN NEW;
END;

BEGIN
TRUNCATE tww_od.network_segment CASCADE; TRUNCATE tww_od.network_node CASCADE;
-- Insert wastewater nodes INSERT INTO tww_od.network_node(node_type, ne_id, geom) SELECT 'wastewater_node', n.obj_id, ST_Force2D(n.situation3d_geometry) FROM tww_od.wastewater_node n;
-- Insert reachpoints INSERT INTO tww_od.network_node(node_type, ne_id, rp_id, geom) SELECT 'reach_point', r.obj_id, -- the reachpoint also keeps a reference to it's reach, as it can be used by blind connections that happen exactly on start/end points rp.obj_id, ST_Force2D(rp.situation3d_geometry) FROM tww_od.reach_point rp JOIN tww_od.reach r ON rp.obj_id = r.fk_reach_point_from OR rp.obj_id = r.fk_reach_point_to;
-- Insert virtual nodes for blind connections INSERT INTO tww_od.network_node(node_type, ne_id, geom) SELECT DISTINCT 'blind_connection', r.obj_id, ST_ClosestPoint(r.progression3d_geometry, rp.situation3d_geometry) FROM tww_od.reach r INNER JOIN tww_od.reach_point rp ON rp.fk_wastewater_networkelement = r.obj_id WHERE ST_LineLocatePoint(ST_CurveToLine(r.progression3d_geometry), rp.situation3d_geometry) NOT IN (0.0, 1.0); -- if exactly at start or at end, we don't need a virtualnode as we have the reachpoint
-- Insert reaches, subdivided according to blind reaches INSERT INTO tww_od.network_segment (segment_type, from_node, to_node, ne_id, geom) SELECT 'reach', sub2.node_id_1, sub2.node_id_2, obj_id, ST_LineSubstring( ST_CurveToLine(ST_Force2D(progression3d_geometry)), ratio_1, ratio_2 ) FROM ( -- This subquery uses LAG to combine a node with the next on a reach. SELECT LAG(sub1.node_id) OVER (PARTITION BY sub1.obj_id ORDER BY sub1.ratio) as node_id_1, sub1.node_id as node_id_2, sub1.progression3d_geometry, LAG(sub1.ratio) OVER (PARTITION BY sub1.obj_id ORDER BY sub1.ratio) as ratio_1, sub1.ratio as ratio_2, obj_id FROM ( -- This subquery joins blind node to reach, with "ratio" being the position of the node along the reach SELECT r.obj_id, r.progression3d_geometry, n.id as node_id, ST_LineLocatePoint(ST_CurveToLine(r.progression3d_geometry), n.geom) AS ratio FROM tww_od.reach r JOIN tww_od.network_node n ON n.ne_id = r.obj_id ) AS sub1 ) AS sub2 WHERE ratio_1 IS NOT NULL AND ratio_1 <> ratio_2;
-- Insert edge between reachpoint (from) to the closest node belonging to the wasterwater network element INSERT INTO tww_od.network_segment (segment_type, from_node, to_node, geom) SELECT DISTINCT ON(n1.id) 'special_structure', n2.id, n1.id, ST_MakeLine(n2.geom, n1.geom) FROM (
SELECT rp.obj_id as rp_obj_id, rp.fk_wastewater_networkelement as wwne_id FROM tww_od.reach_point rp JOIN tww_od.reach r ON rp.obj_id = r.fk_reach_point_from WHERE rp.fk_wastewater_networkelement IS NOT NULL
) AS sub1 JOIN tww_od.network_node as n1 ON n1.rp_id = rp_obj_id JOIN tww_od.network_node as n2 ON n2.ne_id = wwne_id ORDER BY n1.id, ST_Distance(n1.geom, n2.geom);
-- Insert edge between reachpoint (to) to the closest node belonging to the wasterwater network element INSERT INTO tww_od.network_segment (segment_type, from_node, to_node, geom) SELECT DISTINCT ON(n1.id) 'special_structure', n1.id, n2.id, ST_MakeLine(n1.geom, n2.geom) FROM (
SELECT rp.obj_id as rp_obj_id, rp.fk_wastewater_networkelement as wwne_id FROM tww_od.reach_point rp JOIN tww_od.reach r ON rp.obj_id = r.fk_reach_point_to WHERE rp.fk_wastewater_networkelement IS NOT NULL
) AS sub1 JOIN tww_od.network_node as n1 ON n1.rp_id = rp_obj_id JOIN tww_od.network_node as n2 ON n2.ne_id = wwne_id ORDER BY n1.id, ST_Distance(n1.geom, n2.geom);
REFRESH MATERIALIZED VIEW tww_app.vw_network_node; REFRESH MATERIALIZED VIEW tww_app.vw_network_segment;
END;

DECLARE

tbl_name text;
rgx text;
BEGIN
	BEGIN
	   FOR tbl_name,rgx IN (
			SELECT dot.tablename,pfx.prefix||dot.shortcut_en
			FROM information_schema.sequences seq
			LEFT JOIN tww_sys.dictionary_od_table dot ON seq.sequence_name = 'seq_'||dot.tablename||'_oid'
			LEFT JOIN (SELECT prefix  FROM tww_sys.oid_prefixes WHERE active) pfx on True
			WHERE seq.sequence_schema = 'tww_od' AND dot.tablename IS NOT NULL) LOOP
				EXECUTE FORMAT('SELECT SETVAL(''tww_od.seq_%1$I_oid'',(SELECT max(seqs) FROM(
		SELECT RIGHT(obj_id, 6)::int as seqs FROM tww_od.%1$I WHERE  regexp_match(obj_id, ''%2$s\d{6}$'') IS NOT NULL
		UNION
		SELECT last_value as seqs FROM tww_od.seq_%1$I_oid)foo));',tbl_name,rgx);
	   END LOOP;
	END;
END;

DECLARE
schm text;
tbl text;
col text;
BEGIN
IF _undo_existing THEN
	UPDATE tww_od.organisation
	SET tww_active=FALSE;
ELSE NULL;
END IF;
FOR schm,tbl,col IN
	SELECT   tc.table_schema,
		tc.table_name,
		kcu.column_name
	FROM (SELECT constraint_name FROM information_schema.constraint_column_usage
	WHERE table_schema='tww_od'
		AND table_name='organisation'
		AND column_name='obj_id'
		AND constraint_name NOT LIKE'pkey%')ccu
	INNER JOIN information_schema.key_column_usage AS kcu
	ON ccu.constraint_name = kcu.constraint_name
	INNER JOIN information_schema.table_constraints AS tc
		ON tc.constraint_name = kcu.constraint_name
LOOP
	        EXECUTE format($$ UPDATE tww_od.organisation
SET tww_active=TRUE
WHERE obj_id IN (SELECT DISTINCT %I FROM %I.%I)$$, col,schm,tbl);
END LOOP;
END;

DECLARE
	_rp_from_level numeric(7,3);
	_rp_to_level numeric(7,3);

BEGIN

  SELECT rp_from.level INTO _rp_from_level
  FROM tww_od.reach_point rp_from
  WHERE NEW.fk_reach_point_from = rp_from.obj_id;

  SELECT rp_to.level INTO _rp_to_level
  FROM tww_od.reach_point rp_to
  WHERE NEW.fk_reach_point_to = rp_to.obj_id;

  IF _rp_from_level=0 OR _rp_to_level=0 THEN
    NEW.length_effective = ST_Length(NEW.progression3d_geometry);
  ELSE
    NEW.length_effective = COALESCE(sqrt((_rp_from_level - _rp_to_level)^2 + ST_Length(NEW.progression3d_geometry)^2), ST_Length(NEW.progression3d_geometry) );
  END IF;
  RETURN NEW;

END;

DECLARE
  co_obj_id TEXT;
  affected_sp RECORD;
BEGIN
  CASE
    WHEN TG_OP = 'UPDATE' THEN
      co_obj_id = OLD.obj_id;
    WHEN TG_OP = 'INSERT' THEN
      co_obj_id = NEW.obj_id;
    WHEN TG_OP = 'DELETE' THEN
      co_obj_id = OLD.obj_id;
  END CASE;

  SELECT SP.fk_wastewater_structure INTO affected_sp
  FROM tww_od.structure_part SP
  WHERE obj_id = co_obj_id;

  EXECUTE tww_app.update_wastewater_structure_label(affected_sp.fk_wastewater_structure);
  EXECUTE tww_app.update_depth(affected_sp.fk_wastewater_structure);
  EXECUTE tww_app.wastewater_structure_update_fk_main_cover(affected_sp.fk_wastewater_structure);

  RETURN NEW;
END;

DECLARE
  rp_obj_ids TEXT[];
  _ws_obj_id TEXT;
  rps RECORD;
BEGIN
  CASE
    WHEN TG_OP = 'UPDATE' THEN
      rp_obj_ids = ARRAY[OLD.fk_reach_point_from, OLD.fk_reach_point_to];
    WHEN TG_OP = 'INSERT' THEN
      rp_obj_ids = ARRAY[NEW.fk_reach_point_from, NEW.fk_reach_point_to];
    WHEN TG_OP = 'DELETE' THEN
      rp_obj_ids = ARRAY[OLD.fk_reach_point_from, OLD.fk_reach_point_to];
  END CASE;

  FOR _ws_obj_id IN
    SELECT ws.obj_id
      FROM tww_od.wastewater_structure ws
      LEFT JOIN tww_od.wastewater_networkelement ne ON ws.obj_id = ne.fk_wastewater_structure
      LEFT JOIN tww_od.reach_point rp ON ne.obj_id = rp.fk_wastewater_networkelement
      WHERE rp.obj_id = ANY ( rp_obj_ids )
  LOOP
    EXECUTE tww_app.update_wastewater_structure_label(_ws_obj_id);
    EXECUTE tww_app.update_depth(_ws_obj_id);
  END LOOP;

  RETURN NEW;
END;

DECLARE
  rp_obj_id text;
  _ws_obj_id text;
  ne_obj_ids text[];
  ne_obj_id text;
BEGIN
  CASE
    WHEN TG_OP = 'UPDATE' THEN
      IF (NEW.fk_wastewater_networkelement = OLD.fk_wastewater_networkelement) THEN
        RETURN NEW;
      END IF;
      rp_obj_id = OLD.obj_id;
      ne_obj_ids := ARRAY[OLD.fk_wastewater_networkelement, NEW.fk_wastewater_networkelement];
    WHEN TG_OP = 'INSERT' THEN
      rp_obj_id = NEW.obj_id;
      ne_obj_ids := ARRAY[NEW.fk_wastewater_networkelement];
    WHEN TG_OP = 'DELETE' THEN
      rp_obj_id = OLD.obj_id;
      ne_obj_ids := ARRAY[OLD.fk_wastewater_networkelement];
  END CASE;


  UPDATE tww_od.reach
    SET progression3d_geometry = progression3d_geometry
    WHERE fk_reach_point_from = rp_obj_id OR fk_reach_point_to = rp_obj_id; --To retrigger the calculate_length trigger on reach update

  FOREACH ne_obj_id IN ARRAY ne_obj_ids
  LOOP
      SELECT ws.obj_id INTO _ws_obj_id
      FROM tww_od.wastewater_structure ws
      LEFT JOIN tww_od.wastewater_networkelement ne ON ws.obj_id = ne.fk_wastewater_structure
      LEFT JOIN tww_od.reach_point rp ON ne.obj_id = ne_obj_id;

      EXECUTE tww_app.update_wastewater_structure_label(_ws_obj_id);
      EXECUTE tww_app.update_depth(_ws_obj_id);
  END LOOP;

  RETURN NEW;
END;

DECLARE
  _ws_obj_ids TEXT[];
  _ws_obj_id TEXT;
BEGIN
  CASE
    WHEN TG_OP = 'UPDATE' THEN
      _ws_obj_ids = ARRAY[OLD.fk_wastewater_structure, NEW.fk_wastewater_structure];
    WHEN TG_OP = 'INSERT' THEN
      _ws_obj_ids = ARRAY[NEW.fk_wastewater_structure];
    WHEN TG_OP = 'DELETE' THEN
      _ws_obj_ids = ARRAY[OLD.fk_wastewater_structure];
  END CASE;

  FOREACH _ws_obj_id IN ARRAY _ws_obj_ids
  LOOP
    EXECUTE tww_app.update_wastewater_structure_label(_ws_obj_id);
  END LOOP;

  RETURN NEW;
END;

DECLARE
  co_obj_id TEXT;
  affected_sp RECORD;
BEGIN
  CASE
    WHEN TG_OP = 'UPDATE' THEN
      co_obj_id = OLD.obj_id;
    WHEN TG_OP = 'INSERT' THEN
      co_obj_id = NEW.obj_id;
    WHEN TG_OP = 'DELETE' THEN
      co_obj_id = OLD.obj_id;
  END CASE;

  SELECT ne.fk_wastewater_structure INTO affected_sp
  FROM tww_od.wastewater_networkelement ne
  WHERE obj_id = co_obj_id;

  EXECUTE tww_app.update_depth(affected_sp.fk_wastewater_structure);
  EXECUTE tww_app.update_wastewater_structure_label(affected_sp.fk_wastewater_structure);

  RETURN NEW;
END;

DECLARE
  _ws_obj_id TEXT;
BEGIN
  -- Prevent recursion
  IF COALESCE(OLD.identifier, '') = COALESCE(NEW.identifier, '') THEN
    RETURN NEW;
  END IF;
  _ws_obj_id = OLD.obj_id;
  SELECT tww_app.update_wastewater_structure_label(_ws_obj_id) INTO NEW._label;

  IF OLD.fk_main_cover != NEW.fk_main_cover THEN
    EXECUTE tww_app.update_depth(_ws_obj_id);
  END IF;


  RETURN NEW;
END;

DECLARE
  _ne_from_id TEXT;
  _ne_to_id TEXT;
  ch_obj_id TEXT;
BEGIN
  CASE
    WHEN TG_OP = 'UPDATE' THEN
      ch_obj_id = OLD.obj_id;
    WHEN TG_OP = 'INSERT' THEN
      ch_obj_id = NEW.obj_id;
    WHEN TG_OP = 'DELETE' THEN
      ch_obj_id = OLD.obj_id;
  END CASE;

  BEGIN
    SELECT ne.obj_id INTO  _ne_from_id
      FROM tww_od.wastewater_networkelement ch_ne
      LEFT JOIN tww_od.reach re ON ch_ne.obj_id = re.obj_id
      LEFT JOIN tww_od.reach_point rp ON re.fk_reach_point_from = rp.obj_id
      LEFT JOIN tww_od.wastewater_networkelement ne ON rp.fk_wastewater_networkelement = ne.obj_id
      WHERE ch_ne.fk_wastewater_structure = ch_obj_id;
    EXECUTE tww_app.update_wastewater_node_symbology(_ne_from_id);
  EXCEPTION
    WHEN NO_DATA_FOUND THEN
      -- DO NOTHING, THIS CAN HAPPEN
    WHEN TOO_MANY_ROWS THEN
        RAISE EXCEPTION 'TRIGGER ERROR ws_symbology_update_by_channel. Subquery shoud return exactly one row. This is not supposed to happen and indicates an isue with the trigger. The issue must be fixed in TWW.';
  END;

  BEGIN
    SELECT ne.obj_id INTO _ne_to_id
      FROM tww_od.wastewater_networkelement ch_ne
      LEFT JOIN tww_od.reach re ON ch_ne.obj_id = re.obj_id
      LEFT JOIN tww_od.reach_point rp ON re.fk_reach_point_to = rp.obj_id
      LEFT JOIN tww_od.wastewater_networkelement ne ON rp.fk_wastewater_networkelement = ne.obj_id
      WHERE ch_ne.fk_wastewater_structure = ch_obj_id;
    EXECUTE tww_app.update_wastewater_node_symbology(_ne_to_id);
  EXCEPTION
    WHEN NO_DATA_FOUND THEN
      -- DO NOTHING, THIS CAN HAPPEN
    WHEN TOO_MANY_ROWS THEN
        RAISE EXCEPTION 'TRIGGER ERROR ws_symbology_update_by_channel. Subquery shoud return exactly one row. This is not supposed to happen and indicates an isue with the trigger. The issue must be fixed in TWW.';
  END;

  RETURN NEW;
END;

DECLARE
  _ne_id TEXT;
  rp_obj_id TEXT;
BEGIN
  CASE
    WHEN TG_OP = 'UPDATE' THEN
      rp_obj_id = OLD.obj_id;
    WHEN TG_OP = 'INSERT' THEN
      rp_obj_id = NEW.obj_id;
    WHEN TG_OP = 'DELETE' THEN
      rp_obj_id = OLD.obj_id;
  END CASE;

  BEGIN
    SELECT ne.obj_id INTO STRICT _ne_id
      FROM tww_od.wastewater_structure ws
      LEFT JOIN tww_od.wastewater_networkelement ne ON ws.obj_id = ne.fk_wastewater_structure
      LEFT JOIN tww_od.reach_point rp ON ne.obj_id = rp.fk_wastewater_networkelement
      WHERE rp.obj_id = rp_obj_id;
    EXECUTE tww_app.update_wastewater_node_symbology(_ne_id);
  EXCEPTION
    WHEN NO_DATA_FOUND THEN
      -- DO NOTHING, THIS CAN HAPPEN
    WHEN TOO_MANY_ROWS THEN
        RAISE EXCEPTION 'TRIGGER ERROR ws_symbology_update_by_reach_point. Subquery shoud return exactly one row. This is not supposed to happen and indicates an isue with the trigger. The issue must be fixed in TWW.';
  END;

  RETURN NEW;
END;

BEGIN
  CASE
    WHEN TG_OP = 'INSERT' THEN
      NEW.situation3d_geometry = ST_SetSRID( ST_MakePoint( ST_X(NEW.situation3d_geometry), ST_Y(NEW.situation3d_geometry), COALESCE(NEW.level,'NaN') ), 2056);
    WHEN TG_OP = 'UPDATE' THEN
      IF NEW.level <> OLD.level OR (NEW.level IS NULL AND OLD.level IS NOT NULL) OR (NEW.level IS NOT NULL AND OLD.level IS NULL) THEN
        NEW.situation3d_geometry = ST_SetSRID( ST_MakePoint( ST_X(NEW.situation3d_geometry), ST_Y(NEW.situation3d_geometry), COALESCE(NEW.level,'NaN') ), 2056);
      ELSE
        IF ST_Z(NEW.situation3d_geometry) <> ST_Z(OLD.situation3d_geometry) THEN
          NEW.level = NULLIF(ST_Z(NEW.situation3d_geometry),'NaN');
        END IF;
      END IF;
  END CASE;

  RETURN NEW;
END;

BEGIN
  CASE
    WHEN TG_OP = 'INSERT' THEN
      NEW.situation3d_geometry = ST_SetSRID( ST_MakePoint( ST_X(NEW.situation3d_geometry), ST_Y(NEW.situation3d_geometry), COALESCE(NEW.bottom_level,'NaN') ), 2056);
    WHEN TG_OP = 'UPDATE' THEN
      IF NEW.bottom_level <> OLD.bottom_level OR (NEW.bottom_level IS NULL AND OLD.bottom_level IS NOT NULL) OR (NEW.bottom_level IS NOT NULL AND OLD.bottom_level IS NULL) THEN
        NEW.situation3d_geometry = ST_SetSRID( ST_MakePoint( ST_X(NEW.situation3d_geometry), ST_Y(NEW.situation3d_geometry), COALESCE(NEW.bottom_level,'NaN') ), 2056);
      ELSE
        IF ST_Z(NEW.situation3d_geometry) <> ST_Z(OLD.situation3d_geometry) THEN
          NEW.bottom_level = NULLIF(ST_Z(NEW.situation3d_geometry),'NaN');
        END IF;
      END IF;
  END CASE;

  RETURN NEW;
END;

BEGIN
    PERFORM tww_app.reset_od_seqval();
    RETURN NULL;
END;

BEGIN
-- Otherwise this will result in very slow query due to on_structure_part_change_networkelement -- being triggered for all rows. See https://github.com/QGEP/datamodel/pull/166#issuecomment-760245405 //skip-keyword-check IF _all THEN RAISE INFO 'Temporarily disabling symbology and modification triggers'; PERFORM tww_app.alter_symbology_triggers('disable'); PERFORM tww_app.alter_modification_triggers('disable'); END IF;
UPDATE tww_od.wastewater_node n SET _function_hierarchic = function_hierarchic, _usage_current = usage_current, _status = status FROM( SELECT DISTINCT ON (wn.obj_id) wn.obj_id AS wn_obj_id, COALESCE(first_value(CH_from.function_hierarchic) OVER w , first_value(CH_to.function_hierarchic) OVER w) AS function_hierarchic, COALESCE(first_value(CH_from.usage_current) OVER w , first_value(CH_to.usage_current) OVER w) AS usage_current, COALESCE(first_value(ws_node.status) OVER w , first_value(ws_from.status) OVER w , first_value(ws_to.status) OVER w) AS status, rank() OVER w AS hierarchy_rank FROM tww_od.wastewater_node wn LEFT JOIN tww_od.wastewater_networkelement ne ON ne.obj_id = wn.obj_id LEFT JOIN tww_od.wastewater_structure ws_node ON ws_node.obj_id = ne.fk_wastewater_structure
LEFT JOIN tww_od.reach_point rp ON wn.obj_id = rp.fk_wastewater_networkelement LEFT JOIN tww_od.reach re_from ON re_from.fk_reach_point_from = rp.obj_id LEFT JOIN tww_od.wastewater_networkelement ne_from ON ne_from.obj_id = re_from.obj_id LEFT JOIN tww_od.channel CH_from ON CH_from.obj_id = ne_from.fk_wastewater_structure LEFT JOIN tww_od.wastewater_structure ws_from ON ws_from.obj_id = ne_from.fk_wastewater_structure LEFT JOIN tww_vl.channel_function_hierarchic vl_fct_hier_from ON CH_from.function_hierarchic = vl_fct_hier_from.code LEFT JOIN tww_vl.channel_usage_current vl_usg_curr_from ON CH_from.usage_current = vl_usg_curr_from.code
LEFT JOIN tww_od.reach re_to ON re_to.fk_reach_point_to = rp.obj_id LEFT JOIN tww_od.wastewater_networkelement ne_to ON ne_to.obj_id = re_to.obj_id LEFT JOIN tww_od.channel CH_to ON CH_to.obj_id = ne_to.fk_wastewater_structure LEFT JOIN tww_od.wastewater_structure ws_to ON ws_to.obj_id = ne_to.fk_wastewater_structure LEFT JOIN tww_vl.channel_function_hierarchic vl_fct_hier_to ON CH_to.function_hierarchic = vl_fct_hier_to.code LEFT JOIN tww_vl.channel_usage_current vl_usg_curr_to ON CH_to.usage_current = vl_usg_curr_to.code
WHERE _all OR wn.obj_id = _obj_id WINDOW w AS ( PARTITION BY wn.obj_id ORDER BY coalesce(vl_fct_hier_to.tww_symbology_inflow_prio,false) DESC , vl_fct_hier_from.tww_symbology_order ASC NULLS LAST , vl_fct_hier_to.tww_symbology_order ASC NULLS LAST
, vl_usg_curr_from.tww_symbology_order ASC NULLS LAST , vl_usg_curr_to.tww_symbology_order ASC NULLS LAST ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) ) symbology_ne WHERE symbology_ne.wn_obj_id = n.obj_id;
EXECUTE tww_app.update_wn_symbology_by_overflow(_obj_id, _all);
-- See above IF _all THEN RAISE INFO 'Reenabling symbology and modification triggers'; PERFORM tww_app.alter_symbology_triggers('enable'); PERFORM tww_app.alter_modification_triggers('enable'); END IF;
END

BEGIN
-- Otherwise this will result in very slow query due to on_structure_part_change_networkelement -- being triggered for all rows. See https://github.com/QGEP/datamodel/pull/166#issuecomment-760245405 //skip-keyword-check IF _all THEN RAISE INFO 'Temporarily disabling symbology and modification triggers'; PERFORM tww_app.alter_symbology_triggers('disable'); PERFORM tww_app.alter_modification_triggers('disable'); END IF;
UPDATE tww_od.wastewater_node n SET _function_hierarchic = function_hierarchic, _usage_current = usage_current, _status = status FROM( SELECT DISTINCT ON (wn.obj_id) wn.obj_id AS wn_obj_id, COALESCE(first_value(wn._function_hierarchic) OVER w , first_value(wn_from._function_hierarchic) OVER w) AS function_hierarchic, COALESCE(first_value(wn._usage_current) OVER w , first_value(wn_from._usage_current) OVER w) AS usage_current, COALESCE(first_value(wn._status) OVER w , first_value(wn_from._status) OVER w) AS status FROM tww_od.overflow ov LEFT JOIN tww_od.wastewater_node wn ON ov.fk_overflow_to=wn.obj_id LEFT JOIN tww_od.wastewater_networkelement ne_ov ON ne_ov.obj_id = ov.fk_wastewater_node LEFT JOIN tww_vl.channel_function_hierarchic vl_fct_hier ON wn._function_hierarchic = vl_fct_hier.code LEFT JOIN tww_vl.channel_usage_current vl_usg_curr ON wn._usage_current = vl_usg_curr.code
LEFT JOIN tww_od.wastewater_node wn_from ON ne_ov.obj_id = wn_from.obj_id LEFT JOIN tww_vl.channel_function_hierarchic vl_fct_hier_from ON wn_from._function_hierarchic = vl_fct_hier_from.code LEFT JOIN tww_vl.channel_usage_current vl_usg_curr_from ON wn_from._usage_current = vl_usg_curr_from.code WHERE (_all OR wn.obj_id = _obj_id) WINDOW w AS ( PARTITION BY wn.obj_id ORDER BY coalesce(vl_fct_hier.tww_symbology_inflow_prio,false) DESC , vl_fct_hier.tww_symbology_order ASC NULLS LAST , vl_fct_hier_from.tww_symbology_order ASC NULLS LAST
, vl_usg_curr.tww_symbology_order ASC NULLS LAST , vl_usg_curr_from.tww_symbology_order ASC NULLS LAST ROWS BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING) ) symbology_ne WHERE symbology_ne.wn_obj_id = n.obj_id AND TRUE = ANY(array[n._function_hierarchic IS NULL ,n._usage_current IS NULL ,n._status IS NULL]);
-- See above IF _all THEN RAISE INFO 'Reenabling symbology triggers'; PERFORM tww_app.alter_symbology_triggers('enable'); PERFORM tww_app.alter_modification_triggers('enable'); END IF;
END

BEGIN
    DELETE FROM tww_od.file WHERE obj_id = OLD.obj_id;
    RETURN OLD;
  END;

BEGIN
NEW._url = replace(NEW._url, '\', '/');
INSERT INTO tww_od.file( classname, identifier, kind, object, path_relative, fk_dataowner, fk_provider, fk_data_media, remark)
SELECT NEW.classname, NEW.identifier, NEW.kind, NEW.object, SUBSTRING(NEW._url, LENGTH("path")+1, LENGTH(NEW._url)), -- path_relative, NEW.dataowner, -- fk_dataowner, NEW.provider, -- fk_provider, obj_id, -- fk_data_media NEW.remark FROM tww_od.data_media WHERE "path" = SUBSTRING(NEW._url FROM 1 FOR LENGTH("path")) ORDER BY LENGTH("path") DESC LIMIT 1;
-- FOUND is a special variable which is always FALSE at the beginning of a PL/pgsql function and will be set by -- e.g. INSERT to TRUE if at least one row is affected. IF NOT FOUND THEN RAISE WARNING 'Could not insert. File not in repository set in od_data_media!'; END IF;
RETURN NEW; END;

BEGIN
NEW._url = replace(NEW._url, '\', '/');
UPDATE tww_od.file SET classname = NEW.classname, identifier = NEW.identifier, kind = NEW.kind, object = NEW.object, path_relative = SUBSTRING(NEW._url, LENGTH(dm.path)+1, LENGTH(NEW._url)), fk_dataowner = NEW.dataowner, fk_provider = NEW.provider, fk_data_media = dm.id, remark = NEW.remark
FROM ( SELECT obj_id as id, path FROM tww_od.data_media WHERE path = SUBSTRING(NEW._url FROM 1 FOR LENGTH(path)) ORDER BY LENGTH(path) DESC LIMIT 1) dm
WHERE obj_id = OLD.obj_id;
RETURN NEW; END;

DECLARE
  _ne_from_id TEXT;
  _ne_to_id TEXT;
  symb_attribs RECORD;
  re_obj_id TEXT;
BEGIN
  CASE
    WHEN TG_OP = 'UPDATE' THEN
      re_obj_id = OLD.obj_id;
    WHEN TG_OP = 'INSERT' THEN
      re_obj_id = NEW.obj_id;
    WHEN TG_OP = 'DELETE' THEN
      re_obj_id = OLD.obj_id;
  END CASE;

  BEGIN
    SELECT ne.obj_id INTO STRICT _ne_from_id
      FROM tww_od.reach re
      LEFT JOIN tww_od.reach_point rp ON rp.obj_id = re.fk_reach_point_from
      LEFT JOIN tww_od.wastewater_networkelement ne ON ne.obj_id = rp.fk_wastewater_networkelement
      WHERE re.obj_id = re_obj_id;
    EXECUTE tww_app.update_wastewater_node_symbology(_ne_from_id);
  EXCEPTION
    WHEN NO_DATA_FOUND THEN
      -- DO NOTHING, THIS CAN HAPPEN
    WHEN TOO_MANY_ROWS THEN
        RAISE EXCEPTION 'TRIGGER ERROR ws_symbology_update_by_reach. Subquery shoud return exactly one row. This is not supposed to happen and indicates an isue with the trigger. The issue must be fixed in TWW.';
  END;

  BEGIN
    SELECT ne.obj_id INTO STRICT _ne_to_id
      FROM tww_od.reach re
      LEFT JOIN tww_od.reach_point rp ON rp.obj_id = re.fk_reach_point_to
      LEFT JOIN tww_od.wastewater_networkelement ne ON ne.obj_id = rp.fk_wastewater_networkelement
      WHERE re.obj_id = re_obj_id;
    EXECUTE tww_app.update_wastewater_node_symbology(_ne_to_id);
  EXCEPTION
    WHEN NO_DATA_FOUND THEN
      -- DO NOTHING, THIS CAN HAPPEN
    WHEN TOO_MANY_ROWS THEN
        RAISE EXCEPTION 'TRIGGER ERROR ws_symbology_update_by_reach. Subquery shoud return exactly one row. This is not supposed to happen and indicates an isue with the trigger. The issue must be fixed in TWW.';
  END;

  RETURN NEW;
END;
