    LANGUAGE plpgsql
    AS $$
    BEGIN
    CASE
        WHEN OLD.damage_type = 'channel'::tww_app.damage_type THEN DELETE FROM tww_od.damage_channel WHERE obj_id = OLD.obj_id;
      WHEN OLD.damage_type = 'manhole'::tww_app.damage_type THEN DELETE FROM tww_od.damage_manhole WHERE obj_id = OLD.obj_id;
    END CASE;
    DELETE FROM tww_od.damage WHERE obj_id = OLD.obj_id;
    RETURN NULL;
    END;
