          , sludge_bucket = NEW.co_sludge_bucket
          , venting = NEW.co_venting
        WHERE obj_id = OLD.co_obj_id;
      UPDATE tww_od.structure_part sp SET
          fk_dataowner = NEW.fk_dataowner
          , fk_provider = NEW.fk_provider
          -- , fk_wastewater_structure = NEW.co_fk_wastewater_structure
          , identifier = NEW.co_identifier
          , last_modification = NEW.last_modification
          , remark = NEW.co_remark
          , renovation_demand = NEW.co_renovation_demand
        WHERE obj_id = OLD.co_obj_id;
      UPDATE tww_od.wastewater_structure ws SET
          obj_id = NEW.obj_id
          -- , _bottom_label = NEW._bottom_label
          -- , _cover_label = NEW._cover_label
          -- , _depth = NEW._depth
          -- , _input_label = NEW._input_label
          -- , _label = NEW._label
          -- , _output_label = NEW._output_label
          , accessibility = NEW.accessibility
          , condition_score = NEW.condition_score
          , contract_section = NEW.contract_section
          -- , detail_geometry3d_geometry = NEW.detail_geometry3d_geometry
          , elevation_determination = NEW.elevation_determination
          , financing = NEW.financing
          , fk_dataowner = NEW.fk_dataowner
          -- , fk_main_cover = NEW.fk_main_cover
          -- , fk_main_wastewater_node = NEW.fk_main_wastewater_node
          , fk_operator = NEW.fk_operator
          , fk_owner = NEW.fk_owner
          , fk_provider = NEW.fk_provider
          , gross_costs = NEW.gross_costs
