          , urgency_figure = NEW.urgency_figure
          , year_of_construction = NEW.year_of_construction
          , year_of_replacement = NEW.year_of_replacement
        WHERE obj_id = OLD.obj_id;
      UPDATE tww_od.wastewater_node wn SET
          _function_hierarchic = NEW.wn__function_hierarchic
          , _status = NEW.wn__status
          , _usage_current = NEW.wn__usage_current
          , backflow_level_current = NEW.wn_backflow_level_current
