--
-- Name: vw_tww_wastewater_structure; Type: VIEW; Schema: tww_app; Owner: -
--

CREATE VIEW tww_app.vw_tww_wastewater_structure AS
 SELECT ws.identifier,
        CASE
            WHEN (ma.obj_id IS NOT NULL) THEN 'manhole'::text
            WHEN (ss.obj_id IS NOT NULL) THEN 'special_structure'::text
            WHEN (dp.obj_id IS NOT NULL) THEN 'discharge_point'::text
            WHEN (ii.obj_id IS NOT NULL) THEN 'infiltration_installation'::text
            ELSE 'unknown'::text
        END AS ws_type,
    ma.function AS ma_function,
    ss.function AS ss_function,
    ws.fk_owner,
    ws.status,
    ws.obj_id,
    ws._depth,
    ws.accessibility,
    ws.condition_score,
    ws.contract_section,
    ws.elevation_determination,
    ws.financing,
    ws.fk_dataowner,
    ws.fk_operator,
    ws.fk_provider,
    ws.gross_costs,
    ws.inspection_interval,
    ws.last_modification,
    ws.location_name,
    ws.records,
    ws.remark,
    ws.renovation_necessity,
    ws.replacement_value,
    ws.rv_base_year,
    ws.rv_construction_type,
    ws.status_survey_year,
    ws.structure_condition,
    ws.subsidies,
    ws.urgency_figure,
    ws.year_of_construction,
    ws.year_of_replacement,
    main_co_sp.identifier AS co_identifier,
    main_co_sp.remark AS co_remark,
    main_co_sp.renovation_demand AS co_renovation_demand,
    main_co.obj_id AS co_obj_id,
    main_co.brand AS co_brand,
    main_co.cover_shape AS co_shape,
    main_co.diameter AS co_diameter,
    main_co.fastening AS co_fastening,
    main_co.level AS co_level,
    main_co.material AS co_material,
    main_co.positional_accuracy AS co_positional_accuracy,
    main_co.sludge_bucket AS co_sludge_bucket,
    main_co.venting AS co_venting,
    (public.st_force2d(COALESCE(wn.situation3d_geometry, main_co.situation3d_geometry)))::public.geometry(Point,2056) AS situation3d_geometry,
    ma._orientation AS ma_orientation,
    ma.amphibian_exit AS ma_amphibian_exit,
    ma.dimension1 AS ma_dimension1,
    ma.dimension2 AS ma_dimension2,
    ma.material AS ma_material,
    ma.possibility_intervention AS ma_possibility_intervention,
    ma.surface_inflow AS ma_surface_inflow,
    ss.amphibian_exit AS ss_amphibian_exit,
    ss.bypass AS ss_bypass,
    ss.emergency_overflow AS ss_emergency_overflow,
    ss.possibility_intervention AS ss_possibility_intervention,
    ss.stormwater_tank_arrangement AS ss_stormwater_tank_arrangement,
    ss.upper_elevation AS ss_upper_elevation,
    ii.absorption_capacity AS ii_absorption_capacity,
    ii.defects AS ii_defects,
    ii.dimension1 AS ii_dimension1,
    ii.dimension2 AS ii_dimension2,
    ii.distance_to_aquifer AS ii_distance_to_aquifer,
    ii.effective_area AS ii_effective_area,
    ii.emergency_overflow AS ii_emergency_overflow,
    ii.filling_material AS ii_filling_material,
    ii.kind AS ii_kind,
    ii.labeling AS ii_labeling,
    ii.seepage_utilization AS ii_seepage_utilization,
    ii.upper_elevation AS ii_upper_elevation,
    ii.vehicle_access AS ii_vehicle_access,
    ii.watertightness AS ii_watertightness,
    dp.highwater_level AS dp_highwater_level,
    dp.relevance AS dp_relevance,
    dp.terrain_level AS dp_terrain_level,
    dp.upper_elevation AS dp_upper_elevation,
    dp.water_course_number AS dp_water_course_number,
    dp.water_course_segment_canton AS dp_water_course_segment_canton,
    dp.waterlevel_hydraulic AS dp_waterlevel_hydraulic,
    wn.obj_id AS wn_obj_id,
    wn._function_hierarchic AS wn__function_hierarchic,
    wn._status AS wn__status,
    wn._usage_current AS wn__usage_current,
    wn.backflow_level_current AS wn_backflow_level_current,
    wn.bottom_level AS wn_bottom_level,
    wn.elevation_accuracy AS wn_elevation_accuracy,
    wn.fk_hydr_geometry AS wn_fk_hydr_geometry,
    wn.function_node_amelioration AS wn_function_node_amelioration,
    wn.wwtp_number AS wn_wwtp_number,
    ne.fk_dataowner AS wn_fk_dataowner,
    ne.fk_provider AS wn_fk_provider,
    ne.fk_wastewater_structure AS wn_fk_wastewater_structure,
    ne.identifier AS wn_identifier,
    ne.last_modification AS wn_last_modification,
    ne.remark AS wn_remark,
    ws._label,
    ws._cover_label,
    ws._bottom_label,
    ws._input_label,
    ws._output_label,
    wn._usage_current AS _channel_usage_current,
    wn._function_hierarchic AS _channel_function_hierarchic
   FROM (((((((((tww_od.wastewater_structure ws
     LEFT JOIN tww_od.cover main_co ON (((main_co.obj_id)::text = (ws.fk_main_cover)::text)))
     LEFT JOIN tww_od.structure_part main_co_sp ON (((main_co_sp.obj_id)::text = (ws.fk_main_cover)::text)))
     LEFT JOIN tww_od.manhole ma ON (((ma.obj_id)::text = (ws.obj_id)::text)))
     LEFT JOIN tww_od.special_structure ss ON (((ss.obj_id)::text = (ws.obj_id)::text)))
     LEFT JOIN tww_od.discharge_point dp ON (((dp.obj_id)::text = (ws.obj_id)::text)))
     LEFT JOIN tww_od.infiltration_installation ii ON (((ii.obj_id)::text = (ws.obj_id)::text)))
     LEFT JOIN tww_od.wastewater_networkelement ne ON (((ne.obj_id)::text = (ws.fk_main_wastewater_node)::text)))
     LEFT JOIN tww_od.wastewater_node wn ON (((wn.obj_id)::text = (ws.fk_main_wastewater_node)::text)))
     LEFT JOIN tww_od.channel ch ON (((ch.obj_id)::text = (ws.obj_id)::text)))
  WHERE (ch.obj_id IS NULL);


