CREATE VIEW tww_app.import_vw_manhole AS
 SELECT DISTINCT ON (ws.obj_id) ws.obj_id,
    ws.identifier,
    (public.st_force3d(ws.situation3d_geometry))::public.geometry(PointZ,2056) AS situation_geometry,
    ws.co_shape,
