--
-- Name: swmm_link_results; Type: VIEW; Schema: tww_app; Owner: -
--

CREATE VIEW tww_app.swmm_link_results AS
 SELECT ne.fk_wastewater_structure AS ws_obj_id,
    re.obj_id AS re_obj_id,
    re.clear_height,
    re.material,
    mp.obj_id AS mp_obj_id,
    mp.remark AS swmm_simulation_name,
    md.obj_id AS md_obj_id,
    ms.obj_id AS ms_obj_id,
    ms.dimension,
    ms.remark AS swmm_parameter,
    mr.obj_id AS mr_obj_id,
    mr.measuring_duration,
    mr."time",
    mr.value,
    re.progression3d_geometry AS geom
   FROM (((((tww_od.reach re
     JOIN tww_od.wastewater_networkelement ne ON (((re.obj_id)::text = (ne.obj_id)::text)))
     LEFT JOIN tww_od.measuring_point mp ON (((mp.fk_wastewater_structure)::text = (ne.fk_wastewater_structure)::text)))
     LEFT JOIN tww_od.measuring_device md ON (((md.fk_measuring_point)::text = (mp.obj_id)::text)))
     LEFT JOIN tww_od.measurement_series ms ON (((ms.fk_measuring_point)::text = (mp.obj_id)::text)))
     LEFT JOIN tww_od.measurement_result mr ON (((mr.fk_measurement_series)::text = (ms.obj_id)::text)))
  WHERE (md.remark = 'SWMM Simulation'::text)
  ORDER BY mp.obj_id, mr."time";


