     LEFT JOIN tww_od.measuring_point mp ON (((mp.fk_wastewater_structure)::text = (ws.obj_id)::text)))
     LEFT JOIN tww_od.measuring_device md ON (((md.fk_measuring_point)::text = (mp.obj_id)::text)))
