--
-- Name: swmm_vw_storages; Type: VIEW; Schema: tww_app; Owner: -
--

CREATE VIEW tww_app.swmm_vw_storages AS
 SELECT wn.obj_id AS name,
    COALESCE(wn.bottom_level, (0)::numeric) AS invertelev,
    COALESCE((co.level - wn.bottom_level), (0)::numeric) AS maxdepth,
    0 AS initdepth,
        CASE
            WHEN (hr.fk_hydr_geometry IS NOT NULL) THEN 'TABULAR'::text
            ELSE 'FUNCTIONAL'::text
        END AS shape,
        CASE
            WHEN (hr.fk_hydr_geometry IS NOT NULL) THEN concat('storageCurve@', wn.obj_id)
            ELSE '0'::text
        END AS curvecoefficientorcurvename,
    0 AS curveexponent,
    0 AS curveconstant,
    0 AS surchargedepth,
    0 AS fevap,
    NULL::text AS psi,
    NULL::numeric AS ksat,
    NULL::text AS imd,
    ws.identifier AS description,
    concat_ws(','::text, 'special_structure', ssf.value_en) AS tag,
    wn.situation3d_geometry AS geom,
        CASE
            WHEN (ws_st.vsacode = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state,
        CASE
            WHEN (cfhi.vsacode = ANY (ARRAY[5062, 5064, 5066, 5068, 5069, 5070, 5071, 5072, 5074])) THEN 'primary'::text
            ELSE 'secondary'::text
        END AS hierarchy,
    wn.obj_id
   FROM (((((((((tww_od.special_structure ss
     LEFT JOIN tww_od.wastewater_structure ws ON (((ws.obj_id)::text = (ss.obj_id)::text)))
     LEFT JOIN tww_vl.wastewater_structure_status ws_st ON ((ws.status = ws_st.code)))
     LEFT JOIN tww_od.wastewater_networkelement we ON (((we.fk_wastewater_structure)::text = (ws.obj_id)::text)))
     LEFT JOIN tww_od.wastewater_node wn ON (((wn.obj_id)::text = (we.obj_id)::text)))
     LEFT JOIN tww_vl.channel_function_hierarchic cfhi ON ((cfhi.code = wn._function_hierarchic)))
     LEFT JOIN tww_od.hydr_geometry hg ON (((hg.obj_id)::text = (wn.fk_hydr_geometry)::text)))
     LEFT JOIN ( SELECT DISTINCT hydr_geom_relation.fk_hydr_geometry
           FROM tww_od.hydr_geom_relation) hr ON (((hr.fk_hydr_geometry)::text = (hg.obj_id)::text)))
     LEFT JOIN tww_od.cover co ON (((ws.fk_main_cover)::text = (co.obj_id)::text)))
     LEFT JOIN tww_vl.special_structure_function ssf ON ((ss.function = ssf.code)))
  WHERE ((ssf.vsacode = ANY (ARRAY[6397, 6398, 3348, 336, 6478, 3673, 3674, 5574, 3675, 3676, 5575, 5576, 3677])) AND (ws_st.vsacode = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959])))
UNION ALL
 SELECT wn.obj_id AS name,
    COALESCE(wn.bottom_level, (0)::numeric) AS invertelev,
    COALESCE((ii.upper_elevation - wn.bottom_level), (0)::numeric) AS maxdepth,
    0 AS initdepth,
        CASE
            WHEN (hr.fk_hydr_geometry IS NOT NULL) THEN 'TABULAR'::text
            ELSE 'FUNCTIONAL'::text
        END AS shape,
        CASE
            WHEN (hr.fk_hydr_geometry IS NOT NULL) THEN concat('storageCurve@', wn.obj_id)
            ELSE '0'::text
        END AS curvecoefficientorcurvename,
    0 AS curveexponent,
    0 AS curveconstant,
    0 AS surchargedepth,
    0 AS fevap,
    NULL::text AS psi,
    (((((ii.absorption_capacity * (60)::numeric) * (60)::numeric) / (1000)::numeric) / ii.effective_area) * (1000)::numeric) AS ksat,
    NULL::text AS imd,
    ws.identifier AS description,
    concat_ws(','::text, 'infiltration_installation', iik.value_en) AS tag,
    wn.situation3d_geometry AS geom,
        CASE
            WHEN (ws_st.vsacode = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state,
        CASE
            WHEN (cfhi.vsacode = ANY (ARRAY[5062, 5064, 5066, 5068, 5069, 5070, 5071, 5072, 5074])) THEN 'primary'::text
            ELSE 'secondary'::text
        END AS hierarchy,
    wn.obj_id
   FROM ((((((((tww_od.infiltration_installation ii
     LEFT JOIN tww_od.wastewater_structure ws ON (((ws.obj_id)::text = (ii.obj_id)::text)))
     LEFT JOIN tww_vl.wastewater_structure_status ws_st ON ((ws.status = ws_st.code)))
     LEFT JOIN tww_od.wastewater_networkelement we ON (((we.fk_wastewater_structure)::text = (ws.obj_id)::text)))
     LEFT JOIN tww_od.wastewater_node wn ON (((wn.obj_id)::text = (we.obj_id)::text)))
     LEFT JOIN tww_vl.channel_function_hierarchic cfhi ON ((cfhi.code = wn._function_hierarchic)))
     LEFT JOIN tww_od.hydr_geometry hg ON (((hg.obj_id)::text = (wn.fk_hydr_geometry)::text)))
     LEFT JOIN ( SELECT DISTINCT hydr_geom_relation.fk_hydr_geometry
           FROM tww_od.hydr_geom_relation) hr ON (((hr.fk_hydr_geometry)::text = (hg.obj_id)::text)))
     LEFT JOIN tww_vl.infiltration_installation_kind iik ON ((ii.kind = iik.code)))
  WHERE ((iik.vsacode = 276) AND (ws_st.vsacode = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959])))
UNION ALL
 SELECT wn.obj_id AS name,
    COALESCE(wn.bottom_level, (0)::numeric) AS invertelev,
    COALESCE((wn.backflow_level_current - wn.bottom_level), (0)::numeric) AS maxdepth,
    0 AS initdepth,
    'FUNCTIONAL'::text AS shape,
    '0'::text AS curvecoefficientorcurvename,
    0 AS curveexponent,
    0 AS curveconstant,
    0 AS surchargedepth,
    0 AS fevap,
    NULL::text AS psi,
    NULL::numeric AS ksat,
    NULL::text AS imd,
    concat_ws(';'::text, ws.identifier, concat('Storage created for the prank weir: ', pw.obj_id)) AS description,
    'Prank weir'::text AS tag,
    wn.situation3d_geometry AS geom,
        CASE
            WHEN (ws_st.vsacode = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state,
        CASE
            WHEN (cfhi.vsacode = ANY (ARRAY[5062, 5064, 5066, 5068, 5069, 5070, 5071, 5072, 5074])) THEN 'primary'::text
            ELSE 'secondary'::text
        END AS hierarchy,
    wn.obj_id
   FROM (((((((tww_od.prank_weir pw
     LEFT JOIN tww_od.overflow of ON (((pw.obj_id)::text = (of.obj_id)::text)))
     LEFT JOIN tww_od.overflow_char oc ON (((of.fk_overflow_char)::text = (oc.obj_id)::text)))
     LEFT JOIN tww_vl.overflow_char_kind_overflow_char vl_oc_ki ON ((oc.kind_overflow_char = vl_oc_ki.code)))
     LEFT JOIN tww_od.wastewater_node wn ON (((wn.obj_id)::text = (of.fk_wastewater_node)::text)))
     LEFT JOIN tww_od.wastewater_structure ws ON (((ws.fk_main_wastewater_node)::text = (wn.obj_id)::text)))
     LEFT JOIN tww_vl.wastewater_structure_status ws_st ON ((ws.status = ws_st.code)))
     LEFT JOIN tww_vl.channel_function_hierarchic cfhi ON ((cfhi.code = wn._function_hierarchic)))
  WHERE ((ws_st.vsacode = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959])) AND (vl_oc_ki.vsacode = 6220));


