--

CREATE VIEW tww_app.swmm_vw_curves AS
( SELECT (concat('pump_curve@', pu.obj_id))::character varying AS name,
        CASE
            WHEN (row_number() OVER (PARTITION BY pu.obj_id ORDER BY pu.obj_id, hq.altitude) = 1) THEN 'Pump4'::text
            ELSE NULL::text
        END AS type,
    hq.altitude AS xvalue,
    hq.flow AS yvalue,
        CASE
            WHEN (ws_st.vsacode = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state,
        CASE
            WHEN (cfhi.vsacode = ANY (ARRAY[5062, 5064, 5066, 5068, 5069, 5070, 5071, 5072, 5074])) THEN 'primary'::text
            ELSE 'secondary'::text
        END AS hierarchy,
    wn.obj_id
   FROM ((((((((tww_od.hq_relation hq
     LEFT JOIN tww_od.overflow_char oc ON (((hq.fk_overflow_char)::text = (oc.obj_id)::text)))
     LEFT JOIN tww_vl.overflow_char_kind_overflow_char vl_oc_ki ON ((oc.kind_overflow_char = vl_oc_ki.code)))
     LEFT JOIN tww_od.overflow of ON (((of.fk_overflow_char)::text = (oc.obj_id)::text)))
     LEFT JOIN tww_od.pump pu ON (((pu.obj_id)::text = (of.obj_id)::text)))
     LEFT JOIN tww_od.wastewater_node wn ON (((wn.obj_id)::text = (of.fk_wastewater_node)::text)))
