--
-- Name: swmm_vw_losses; Type: VIEW; Schema: tww_app; Owner: -
--

CREATE VIEW tww_app.swmm_vw_losses AS
 SELECT DISTINCT re.obj_id AS link,
    (0)::double precision AS kentry,
    (0)::double precision AS kexit,
    (0)::double precision AS kavg,
        CASE
            WHEN (ts.obj_id IS NOT NULL) THEN 'YES'::text
            ELSE 'NO'::text
        END AS flap_gate,
    (0)::double precision AS seepage,
        CASE
            WHEN (ws_st.vsacode = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state,
        CASE
            WHEN (ch_fhi.vsacode = ANY (ARRAY[5062, 5064, 5066, 5068, 5069, 5070, 5071, 5072, 5074])) THEN 'primary'::text
            ELSE 'secondary'::text
        END AS hierarchy,
    re.obj_id
   FROM (((((((((tww_od.reach re
     LEFT JOIN tww_od.wastewater_networkelement ne ON (((ne.obj_id)::text = (re.obj_id)::text)))
     LEFT JOIN tww_od.pipe_profile pp ON (((pp.obj_id)::text = (re.fk_pipe_profile)::text)))
     LEFT JOIN tww_od.reach_point rp_from ON (((rp_from.obj_id)::text = (re.fk_reach_point_from)::text)))
     LEFT JOIN tww_od.wastewater_node from_wn ON (((from_wn.obj_id)::text = (rp_from.fk_wastewater_networkelement)::text)))
     LEFT JOIN tww_od.throttle_shut_off_unit ts ON (((ts.fk_wastewater_node)::text = (from_wn.obj_id)::text)))
     LEFT JOIN tww_od.wastewater_structure ws ON (((ws.obj_id)::text = (ne.fk_wastewater_structure)::text)))
     LEFT JOIN tww_od.channel ch ON (((ws.obj_id)::text = (ch.obj_id)::text)))
     LEFT JOIN tww_vl.wastewater_structure_status ws_st ON ((ws.status = ws_st.code)))
     LEFT JOIN tww_vl.channel_function_hierarchic ch_fhi ON ((ch.function_hierarchic = ch_fhi.code)))
  WHERE (ws_st.vsacode = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959]));


