CREATE VIEW tww_app.swmm_vw_outlets AS
 SELECT concat('outlet@', pw.obj_id) AS name,
    of.fk_wastewater_node AS fromnode,
    of.fk_overflow_to AS tonode,
    0 AS "offset",
    'TABULAR/DEPTH'::text AS type,
    concat('prank_weir_curve@', pw.obj_id) AS qtable_qcoeff,
    NULL::text AS qexpon,
    'NO'::text AS gated,
        CASE
            WHEN (ws_st.vsacode = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state,
        CASE
            WHEN (cfhi.vsacode = ANY (ARRAY[5062, 5064, 5066, 5068, 5069, 5070, 5071, 5072, 5074])) THEN 'primary'::text
            ELSE 'secondary'::text
        END AS hierarchy,
    wn.obj_id
   FROM (((((((tww_od.prank_weir pw
     LEFT JOIN tww_od.overflow of ON (((pw.obj_id)::text = (of.obj_id)::text)))
     LEFT JOIN tww_od.overflow_char oc ON (((of.fk_overflow_char)::text = (oc.obj_id)::text)))
     LEFT JOIN tww_vl.overflow_char_kind_overflow_char vl_oc_ki ON ((oc.kind_overflow_char = vl_oc_ki.code)))
     LEFT JOIN tww_od.wastewater_node wn ON (((wn.obj_id)::text = (of.fk_wastewater_node)::text)))
     LEFT JOIN tww_od.wastewater_structure ws ON (((ws.fk_main_wastewater_node)::text = (wn.obj_id)::text)))
     LEFT JOIN tww_vl.wastewater_structure_status ws_st ON ((ws.status = ws_st.code)))
     LEFT JOIN tww_vl.channel_function_hierarchic cfhi ON ((cfhi.code = wn._function_hierarchic)))
  WHERE ((ws_st.vsacode = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959])) AND (vl_oc_ki.vsacode = 6220));


