-- Name: swmm_vw_polygons; Type: VIEW; Schema: tww_app; Owner: -
--

CREATE VIEW tww_app.swmm_vw_polygons AS
 SELECT foo.subcatchment,
    round((public.st_x((foo.dp).geom))::numeric, 2) AS x_coord,
    round((public.st_y((foo.dp).geom))::numeric, 2) AS y_coord,
    foo.state,
    foo.hierarchy,
    foo.obj_id
   FROM ( SELECT swmm_vw_subcatchments.name AS subcatchment,
            public.st_dumppoints(swmm_vw_subcatchments.geom) AS dp,
            public.st_npoints(swmm_vw_subcatchments.geom) AS nvert,
            swmm_vw_subcatchments.state,
            swmm_vw_subcatchments.hierarchy,
            swmm_vw_subcatchments.obj_id
           FROM tww_app.swmm_vw_subcatchments) foo
  WHERE ((foo.dp).path[2] <> foo.nvert);


--
-- Name: swmm_vw_pumps; Type: VIEW; Schema: tww_app; Owner: -
--

CREATE VIEW tww_app.swmm_vw_pumps AS
 SELECT pu.obj_id AS name,
    of.fk_wastewater_node AS fromnode,
    of.fk_overflow_to AS tonode,
    (concat('pump_curve@', pu.obj_id))::character varying AS pumpcurve,
    'ON'::character varying AS status,
    pu.start_level AS startupdepth,
    pu.stop_level AS shutoffdepth,
    concat_ws(';'::text, of.identifier,
        CASE
            WHEN (oc.obj_id IS NULL) THEN 'No curve will be created for this pump, it has no overflow_characteristic'::text
            WHEN (vl_oc_ki.vsacode <> 6220) THEN concat(pu.obj_id, 'No curve will be created for this pump, kind_overflow_char is not equal to H/Q, Q/Q relations are not supported by SWMM')
            ELSE NULL::text
        END) AS description,
    (pu.obj_id)::character varying AS tag,
        CASE
            WHEN (ws_st.vsacode = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state,
        CASE
            WHEN (cfhi.vsacode = ANY (ARRAY[5062, 5064, 5066, 5068, 5069, 5070, 5071, 5072, 5074])) THEN 'primary'::text
            ELSE 'secondary'::text
        END AS hierarchy,
    wn.obj_id
   FROM (((((((tww_od.pump pu
     LEFT JOIN tww_od.overflow of ON (((pu.obj_id)::text = (of.obj_id)::text)))
     LEFT JOIN tww_od.overflow_char oc ON (((of.fk_overflow_char)::text = (oc.obj_id)::text)))
     LEFT JOIN tww_vl.overflow_char_kind_overflow_char vl_oc_ki ON ((oc.kind_overflow_char = vl_oc_ki.code)))
     LEFT JOIN tww_od.wastewater_node wn ON (((wn.obj_id)::text = (of.fk_wastewater_node)::text)))
     LEFT JOIN tww_od.wastewater_structure ws ON (((ws.fk_main_wastewater_node)::text = (wn.obj_id)::text)))
     LEFT JOIN tww_vl.wastewater_structure_status ws_st ON ((ws.status = ws_st.code)))
     LEFT JOIN tww_vl.channel_function_hierarchic cfhi ON ((cfhi.code = wn._function_hierarchic)))
  WHERE (ws_st.vsacode = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959]));


