-- Name: swmm_vw_xsections; Type: VIEW; Schema: tww_app; Owner: -
--

CREATE VIEW tww_app.swmm_vw_xsections AS
 SELECT DISTINCT re.obj_id AS link,
        CASE
            WHEN (ppt.vsacode = 3350) THEN 'CIRCULAR'::text
            WHEN (ppt.vsacode = 3353) THEN 'RECT_CLOSED'::text
            WHEN (ppt.vsacode = 3351) THEN 'EGG'::text
            WHEN (ppt.vsacode = 3355) THEN 'CUSTOM'::text
            WHEN (ppt.vsacode = 3352) THEN 'ARCH'::text
            WHEN (ppt.vsacode = 3354) THEN 'PARABOLIC'::text
            ELSE 'CIRCULAR'::text
        END AS shape,
        CASE
            WHEN (re.clear_height = 0) THEN (0.1)::double precision
            WHEN (re.clear_height IS NULL) THEN (0.1)::double precision
            ELSE ((re.clear_height)::double precision / (1000)::double precision)
        END AS geom1,
        CASE
            WHEN (ppt.vsacode = ANY (ARRAY[3352, 3353, 3354])) THEN
            CASE
                WHEN (pp.height_width_ratio IS NOT NULL) THEN
                CASE
                    WHEN (re.clear_height = 0) THEN ((0.1 / pp.height_width_ratio))::double precision
                    WHEN (re.clear_height IS NULL) THEN ((0.1 / pp.height_width_ratio))::double precision
                    ELSE (((re.clear_height)::double precision / (1000)::double precision) / (pp.height_width_ratio)::double precision)
                END
                WHEN (pp.height_width_ratio IS NULL) THEN (0.002)::double precision
                ELSE (0.002)::double precision
            END
            ELSE NULL::double precision
        END AS geom2,
    0 AS geom3,
    0 AS geom4,
    1 AS barrels,
    NULL::text AS culvert,
        CASE
            WHEN (ws_st.vsacode = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state,
        CASE
            WHEN (cfhi.vsacode = ANY (ARRAY[5062, 5064, 5066, 5068, 5069, 5070, 5071, 5072, 5074])) THEN 'primary'::text
            ELSE 'secondary'::text
        END AS hierarchy,
    re.obj_id
   FROM (((((((tww_od.reach re
     LEFT JOIN tww_od.pipe_profile pp ON (((pp.obj_id)::text = (re.fk_pipe_profile)::text)))
     LEFT JOIN tww_vl.pipe_profile_profile_type ppt ON ((ppt.code = pp.profile_type)))
     LEFT JOIN tww_od.wastewater_networkelement ne ON (((ne.obj_id)::text = (re.obj_id)::text)))
     LEFT JOIN tww_od.wastewater_structure ws ON (((ws.obj_id)::text = (ne.fk_wastewater_structure)::text)))
     LEFT JOIN tww_vl.wastewater_structure_status ws_st ON ((ws.status = ws_st.code)))
     LEFT JOIN tww_od.channel ch ON (((ch.obj_id)::text = (ws.obj_id)::text)))
     LEFT JOIN tww_vl.channel_function_hierarchic cfhi ON ((cfhi.code = ch.function_hierarchic)))
  WHERE (ws_st.vsacode = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959]))
UNION ALL
 SELECT pw.obj_id AS link,
    'RECT_OPEN'::text AS shape,
    (pw.level_max - pw.level_min) AS geom1,
    pw.hydraulic_overflow_length AS geom2,
    NULL::integer AS geom3,
    NULL::integer AS geom4,
    NULL::integer AS barrels,
    NULL::text AS culvert,
        CASE
            WHEN (ws_st.vsacode = ANY (ARRAY[7959, 6529, 6526])) THEN 'planned'::text
            ELSE 'current'::text
        END AS state,
        CASE
            WHEN (cfhi.vsacode = ANY (ARRAY[5062, 5064, 5066, 5068, 5069, 5070, 5071, 5072, 5074])) THEN 'primary'::text
            ELSE 'secondary'::text
        END AS hierarchy,
    wn.obj_id
   FROM ((((((tww_od.prank_weir pw
     LEFT JOIN tww_od.overflow of ON (((pw.obj_id)::text = (of.obj_id)::text)))
     LEFT JOIN tww_od.overflow_char oc ON (((of.fk_overflow_char)::text = (oc.obj_id)::text)))
     LEFT JOIN tww_od.wastewater_node wn ON (((wn.obj_id)::text = (of.fk_wastewater_node)::text)))
     LEFT JOIN tww_od.wastewater_structure ws ON (((ws.fk_main_wastewater_node)::text = (wn.obj_id)::text)))
     LEFT JOIN tww_vl.wastewater_structure_status ws_st ON ((ws.status = ws_st.code)))
     LEFT JOIN tww_vl.channel_function_hierarchic cfhi ON ((cfhi.code = wn._function_hierarchic)))
  WHERE ((ws_st.vsacode = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959])) OR ((oc.kind_overflow_char <> 6220) AND (ws_st.vsacode = ANY (ARRAY[6530, 6533, 8493, 6529, 6526, 7959]))));


