    bio_ecol_assessment.comparison_last,
    bio_ecol_assessment.date_last_examen,
    bio_ecol_assessment.impact_auxiliary_indic,
    bio_ecol_assessment.impact_external_aspect,
    bio_ecol_assessment.impact_macroinvertebrates,
    bio_ecol_assessment.impact_water_plants,
    bio_ecol_assessment.intervention_demand,
    bio_ecol_assessment.io_calculation,
    bio_ecol_assessment.outlet_pipe_clear_height,
