--

CREATE VIEW tww_app.vw_tww_reach AS
 SELECT re.obj_id,
    re.clear_height,
    re.material,
    ch.usage_current AS ch_usage_current,
    ch.function_hierarchic AS ch_function_hierarchic,
    ws.status AS ws_status,
    ws.fk_owner AS ws_fk_owner,
    ch.function_hydraulic AS ch_function_hydraulic,
        CASE
            WHEN (pp.height_width_ratio IS NOT NULL) THEN ((round(((re.clear_height)::numeric / pp.height_width_ratio)))::smallint)::integer
            ELSE re.clear_height
        END AS width,
        CASE
            WHEN ((rp_from.level > (0)::numeric) AND (rp_to.level > (0)::numeric)) THEN round((((rp_from.level - rp_to.level) / (public.st_length(re.progression3d_geometry))::numeric) * (1000)::numeric), 1)
            ELSE NULL::numeric
        END AS _slope_per_mill,
    re.coefficient_of_friction,
    re.elevation_determination,
    re.fk_pipe_profile,
    re.flow_time_dry_weather,
    re.horizontal_positioning,
    re.hydraulic_load_current,
    re.inside_coating,
    re.leak_protection,
    re.length_effective,
    re.progression3d_geometry,
    re.reliner_material,
    re.reliner_nominal_size,
