--
-- Name: vw_tww_damage; Type: VIEW; Schema: tww_app; Owner: -
--

CREATE VIEW tww_app.vw_tww_damage AS
 SELECT
        CASE
            WHEN (channel.obj_id IS NOT NULL) THEN 'channel'::tww_app.damage_type
            WHEN (manhole.obj_id IS NOT NULL) THEN 'manhole'::tww_app.damage_type
            ELSE 'damage'::tww_app.damage_type
        END AS damage_type,
    damage.obj_id,
    damage.comments,
    damage.connection,
    damage.fk_dataowner,
    damage.fk_examination,
    damage.fk_provider,
    damage.last_modification,
    damage.line_damage,
    damage.single_damage_class,
    damage.video_counter,
    damage.view_parameters,
    channel.channel_damage_begin,
    channel.channel_damage_code,
    channel.channel_damage_end,
    channel.channel_distance,
    channel.channel_quantification1,
    channel.channel_quantification2,
    manhole.manhole_channel_distance,
    manhole.manhole_damage_begin,
    manhole.manhole_damage_code,
    manhole.manhole_damage_end,
    manhole.manhole_quantification1,
    manhole.manhole_quantification2,
    manhole.manhole_shaft_area
   FROM ((tww_od.damage damage
     LEFT JOIN tww_od.damage_channel channel ON (((channel.obj_id)::text = (damage.obj_id)::text)))
     LEFT JOIN tww_od.damage_manhole manhole ON (((manhole.obj_id)::text = (damage.obj_id)::text)));


